INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000288', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Ahmadi2015', 'Dim object tracking in a heavy clutter environment is a theoretical and technological challenge in the field of image processing. For a small dim object, conventional tracking methods fail for the lack of geometrical information. Multiple hypotheses testing (MHT) is one of the generally accepted methods in target tracking systems. However, processing a tree structure with a significant number of branches in MHT has been a challenging issue. Tracking high-speed objects with traditional MHT requires some presumptions which limit the capabilities of these methods. This study presents a hierarchal tracking system in two levels to solve this problem. For each point in the lower-level, a multi objective particle swarm optimisation technique is applied to a group of consecutive frames to reduce the number of branches in each tracking tree. Thus, an optimum track for each moving object is obtained in a group of frames. In the upper-level, an iterative process is used to connect the matching optimum tracks of the consecutive frames based on the spatial information and fitness values. The experimental results show that the proposed method has a superior performance in relation to some common dim object tracking methods over different image sequence data sets.', NULL, NULL, 'K. Ahmadi and E. Salari', NULL, NULL, NULL, NULL, '10.1049/iet-ipr.2014.0927', NULL, NULL, NULL, NULL, NULL, NULL, 'IET Image Processing', NULL, 'clutter;image matching;image sequences;iterative methods;object tracking;particle swarm optimisation;target tracking;MHT;geometrical information;heavy clutter environment;image matching;image processing;image sequence data set;iterative process;multiobjective particle swarm optimisation technique;multiple hypotheses testing;small dim object tracking;target tracking system;tree structure', NULL, NULL, NULL, NULL, '9', NULL, '820-826', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Small dim object tracking using a multi objective particle swarm optimisation technique', NULL, NULL, '9', '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Ahmadi2015', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000289', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Alipour:2013:CAT:2487085.2487123', NULL, 'Piscataway, NJ, USA', NULL, 'Alipour, Anahita and Hindle, Abram and Stroulia, Eleni', 'Proceedings of the 10th Working Conference on Mining Software Repositories', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'San Francisco, CA, USA', NULL, NULL, NULL, NULL, '183--192', NULL, NULL, NULL, NULL, 'IEEE Press', NULL, NULL, NULL, NULL, NULL, 'MSR ''13', 'A Contextual Approach Towards More Accurate Duplicate Bug Report Detection', NULL, 'http://dl.acm.org/citation.cfm?id=2487085.2487123', NULL, '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Alipour:2013:CAT:2487085.2487123', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000291', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'An2014', 'A typical bug fixing cycle involves the reporting of a bug, the triaging of the report, the production and verification of a fix, and the closing of the bug. However, previous work has studied two phenomena where more than one fix are associated with the same bug report. The first one is the case where developers re-open a previously fixed bug in the bug repository (sometimes even multiple times) to provide a new bug fix that replace a previous fix, whereas the second one is the case where multiple commits in the version control system contribute to the same bug report (\"supplementary bug fixes\"). Even though both phenomena seem related, they have never been studied together, i.e., are supplementary fixes a subset of re-opened bugs or the other way around? This paper investigates the interplay between both phenomena in five open source software projects: Mozilla, Net beans, Eclipse JDT Core, Eclipse Platform SWT, and Web Kit. We found that re-opened bugs account for between 21.6% and 33.8% of all supplementary fixes. However, 33% to 57.5% of re-opened bugs had only one commit associated, which means that the original bug report was prematurely closed instead of fixed incorrectly. Furthermore, we constructed predictive models for re-opened bugs using historical information about supplementary bug fixes with a precision between 72.2% and 97%, as well as a recall between 47.7% and 65.3%. Software researchers and practitioners who are mining data repositories can use our approach to identify potential failures of their bug fixes and the re-opening of bug reports.', NULL, NULL, 'L. An and F. Khomh and B. Adams', 'Source Code Analysis and Manipulation (SCAM), 2014 IEEE 14th International Working Conference on', NULL, NULL, NULL, '10.1109/SCAM.2014.29', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'data mining;program debugging;public domain software;Eclipse JDT Core;Eclipse Platform SWT;Mozilla;Netbeans;WebKit;bug fixing cycle;bug report;mining data repositories;open source software projects;reopened bug subset;supplementary fix;version control system;Computer bugs;Control systems;Data mining;History;Open source software;Predictive models;mining software repositories;prediction model;re-opened bugs;supplementary fixes', NULL, NULL, 'Sept', NULL, NULL, NULL, '205-214', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Supplementary Bug Fixes vs. Re-opened Bugs', NULL, NULL, NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'An2014', '1');
