INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000293', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Antoniol2005', 'Versioning system repositories and bug tracking systems are valuable sources of information to study the evolution of large open source software systems. However, being conceived for specific purposes, i.e., to support the development or trigger maintenance activities, they do neither allow an easy information browsing nor support the study of software evolution. For example, queries such as locating and browsing the faultiest methods are not provided. This paper addresses such issues and proposes an approach and a framework to consistently merge information extracted from source code, versioning repositories and bug reports. Our information representation exploits the property concepts of the FAMIX information exchange meta-model, allowing to represent, browse, and query, at different level of abstractions, the concept of interest. This allows the user to navigate back and forth from versioning system modification reports to bug reports and to source code. This paper presents the analysis framework and approaches to populate it, tools developed and under development for it, as well as lessons learned while analyzing several releases of Mozilla. © 2005 Elsevier B.V.', NULL, NULL, 'Antoniol, G.a and Di Penta, M.a and Gall, H.b and Pinzger, M.c ', NULL, NULL, NULL, NULL, '10.1016/j.entcs.2004.08.036', NULL, NULL, NULL, NULL, NULL, NULL, 'Electronic Notes in Theoretical Computer Science', NULL, NULL, NULL, NULL, NULL, 'cited By 14', '3', NULL, '87-99', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Towards the integration of versioning systems, bug reports and source code meta-models', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-15544385242&partnerID=40&md5=ec6190f1286c80cb78380c54b480ea58', '127', '2005', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Antoniol2005', '1');
