INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000303', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Banerjee2015a', 'Bug tracking systems play an important role in the development and maintenance of large-scale software systems. Having access to open source bug tracking systems has allowed researchers to take advantage of rich datasets and propose solutions to manage duplicate report classification, developer assignment and quality assessment. In spite of research advances, our understanding of the content of these repositories remains limited, primarily because of their size. In many cases, researchers analyze small portions of datasets thus limiting the understanding of the dynamics of problem reporting. The objective of this study is to explore the properties of two large-scale open source problem report repositories. The Eclipse dataset, at the time of download, consisted of 363; 770 reports spanning 11+ years, whereas Mozilla contained 699; 085 reports spanning 14+ years.Our research examines the evolution of datasets over time by analyzing the changes in the repository and the profiles of users who submit problem reports. We provide quantitative evidence on how submitter''s maturity reduces the propensity to submit poor quality, insignificant or duplicate reports. We show that a diverse user base, characteristic of Mozilla, creates challenges for the development team as they spend more time triaging, rather than fixing, issues. Finally, we provide the research community with a series of observations and suggestions on how to study large-scale problem repositories.', NULL, NULL, 'S. Banerjee and J. Helmick and Z. Syed and B. Cukic', '2015 IEEE 16th International Symposium on High Assurance Systems Engineering', NULL, NULL, NULL, '10.1109/HASE.2015.45', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program debugging;public domain software;software maintenance;Eclipse dataset;Mozilla characteristic;Mozilla dataset;developer assignment;download time;duplicate report classification management;large-scale open source problem report repositories;large-scale software system development;large-scale software system maintenance;open source bug tracking systems;problem reporting dynamics;quality assessment;quantitative analysis;research community;user base;Communities;Computer bugs;Educational institutions;Maintenance engineering;Pragmatics;Quality assessment;Software systems', NULL, NULL, 'Jan', NULL, NULL, NULL, '263-270', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Eclipse vs. Mozilla: A Comparison of Two Large-Scale Open Source Problem Report Repositories', NULL, NULL, NULL, '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Banerjee2015a', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000304', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Bangcharoensap:2012:LSC:2419061.2419428', NULL, 'Washington, DC, USA', NULL, 'Bangcharoensap, Phiradet and Ihara, Akinori and Kamei, Yasutaka and Matsumoto, Ken-ichi', 'Proceedings of the 2012 Fourth International Workshop on Empirical Software Engineering in Practice', NULL, NULL, NULL, '10.1109/IWESEP.2012.14', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Bug localization, Text mining, Code mining, Change history mining', NULL, NULL, NULL, NULL, NULL, NULL, '10--15', NULL, NULL, NULL, NULL, 'IEEE Computer Society', NULL, NULL, NULL, NULL, NULL, 'IWESEP ''12', 'Locating Source Code to Be Fixed Based on Initial Bug Reports - A Case Study on the Eclipse Project', NULL, 'http://dx.doi.org/10.1109/IWESEP.2012.14', NULL, '2012', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Bangcharoensap:2012:LSC:2419061.2419428', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000306', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Banitaan2013', 'Bug Tracking System (BTS) is public ally accessible which enables geographically distributed developers to follow the work of each other and contribute in bug fixing. Developer interactions through commenting on bug reports generate a developer social network that can be used to improve software development and maintenance activities. In large scale complex software projects, software maintenance requires larger groups to participate in its activities. Most previous bug assignments approaches assign only one developer to new bugs. However, bug fixing is a collaborative effort between several developers (i.e., many developers contribute their experience in fixing a bug report). In this work, we build developers social networks based on developers comments on bug reports and detect developers communities. We also assign a relevant community to each newly committed bug report. Moreover, we rank developers in each community based on their experience. An experimental evaluation is conducted on three open source projects namely Net Beans, Free desktop, and Mandriva. The results show that the detected communities are significantly connected with high density. They also show that the proposed approach achieves feasible accuracy of bug assignment.', NULL, NULL, 'S. Banitaan and M. Alenezi', 'Machine Learning and Applications (ICMLA), 2013 12th International Conference on', NULL, NULL, NULL, '10.1109/ICMLA.2013.107', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'groupware;program debugging;project management;public domain software;social networking (online);software maintenance;BTS;DECOBA;Freedesktop project;Mandriva project;NetBeans project;bug assignment;bug fixing;bug reports;bug tracking system;developer communities;developer interactions;developer social network;large-scale complex software projects;open source projects;software development activity improvement;software maintenance activity improvement;Collaboration;Communities;Computer bugs;Measurement;Predictive models;Social network services;Vectors;Bug Report Assignment;Community Detection;Developer Ranking;Developers Social Network', NULL, NULL, 'Dec', NULL, NULL, NULL, '66-71', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'DECOBA: Utilizing Developers Communities in Bug Assignment', NULL, NULL, '2', '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Banitaan2013', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000307', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Baysal:2013:SAP:2486788.2486957', NULL, 'Piscataway, NJ, USA', NULL, 'Baysal, Olga and Holmes, Reid and Godfrey, Michael W.', 'Proceedings of the 2013 International Conference on Software Engineering', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'San Francisco, CA, USA', NULL, NULL, NULL, NULL, '1185--1188', NULL, NULL, NULL, NULL, 'IEEE Press', NULL, NULL, NULL, NULL, NULL, 'ICSE ''13', 'Situational Awareness: Personalizing Issue Tracking Systems', NULL, 'http://dl.acm.org/citation.cfm?id=2486788.2486957', NULL, '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Baysal:2013:SAP:2486788.2486957', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000308', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Baysal2012', 'Bug triaging is an error-prone, tedious and time-consuming task. However, little qualitative research has been done on the actual use of bug tracking systems, bug triage, and resolution processes. We are planning to conduct a qualitative study to understand the dynamics of bug triage and fixing process, as well as bug reassignments and reopens. We will study interviews conducted with Mozilla Core and Firefox developers to get insights into the primary obstacles developers face during the bug fixing process. Is the triage process flawed? Does bug review slow things down? Does approval takes too long? We will also categorize the main reasons for bug reassignments and reopens. We will then combine results with a quantitative study of Firefox bug reports, focusing on factors related to bug report edits and number of people involved in handling the bug.', NULL, NULL, 'O. Baysal and R. Holmes and M. W. Godfrey', 'User Evaluation for Software Engineering Researchers (USER), 2012', NULL, NULL, NULL, '10.1109/USER.2012.6226578', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program debugging;Mozilla core;Mozilla developers;bug fixing process;bug reassignments;bug reopens;bug tracking systems;bug triage;resolution practices;Computer bugs;Educational institutions;Encyclopedias;Fires;Interviews;Software;Sorting', NULL, NULL, 'June', NULL, NULL, NULL, '29-30', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Revisiting bug triage and resolution practices', NULL, NULL, NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Baysal2012', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000309', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Baysal2013', 'When submitting a patch, the primary concerns of individual developers are “How can I maximize the chances of my patch being approved, and minimize the time it takes for this to happen?” In principle, code review is a transparent process that aims to assess qualities of the patch by their technical merits and in a timely manner; however, in practice the execution of this process can be affected by a variety of factors, some of which are external to the technical content of the patch itself. In this paper, we describe an empirical study of the code review process for WebKit, a large, open source project; we replicate the impact of previously studied factors - such as patch size, priority, and component and extend these studies by investigating organizational (the company) and personal dimensions (reviewer load and activity, patch writer experience) on code review response time and outcome. Our approach uses a reverse engineered model of the patch submission process and extracts key information from the issue tracking and code review systems. Our findings suggest that these nontechnical factors can significantly impact code review outcomes.', NULL, NULL, 'O. Baysal and O. Kononenko and R. Holmes and M. W. Godfrey', '2013 20th Working Conference on Reverse Engineering (WCRE)', NULL, NULL, NULL, '10.1109/WCRE.2013.6671287', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program diagnostics;reverse engineering;software quality;WebKit;code review outcome;code review process;code review response time;code review system;component;issue tracking system;nontechnical factors;open source project;patch size;patch submission process;priority;reverse engineered model;software quality;Browsers;Companies;Databases;Electronic mail;Google;Time factors;Code review;WebKit;non-technical factors;open source software;personal and organizational aspects', NULL, NULL, 'Oct', NULL, NULL, NULL, '122-131', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The influence of non-technical factors on code review', NULL, NULL, NULL, '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Baysal2013', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000310', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Beck2015', 'Locating features in large software systems is a fundamental maintenance task for developers when fixing bugs and extending software. We introduce In Situ Impact Insight (I3), a novel user interface to support feature location. In addition to a list of search results, I3 provides support for developers during browsing and inspecting the retrieved code entities. In situ visualizations augment results and source code with additional information relevant for further exploration. Developers are able to retrieve details on the textual similarity of a source code entity to the search query and to other entities, as well as the information on co-changed entities from a project''s history. Execution traces recorded during program runs can be used as filters to further refine the search results. We implemented I3 as an Eclipse plug-in and tested it in a user study involving 18 students and professional developers that were asked to perform three feature location tasks chosen from the issue tracking system of jEdit. The results of our study suggest that I3''s user interface is intuitive and unobtrusively supports developers with the required information when and where they need it.', NULL, NULL, 'F. Beck and B. Dit and J. Velasco-Madden and D. Weiskopf and D. Poshyvanyk', '2015 IEEE 23rd International Conference on Program Comprehension', NULL, NULL, NULL, '10.1109/ICPC.2015.24', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program diagnostics;program visualisation;query processing;software maintenance;source code (software);user interfaces;Eclipse plug-in;I3;I3 user interface;In Situ Impact Insight;feature location;in situ visualizations;jEdit tracking system;large software systems;program execution traces;retrieved code entity;search query;source code entity textual similarity;Feature extraction;History;Maintenance engineering;Software systems;User interfaces;Visualization', NULL, NULL, 'May', NULL, NULL, NULL, '151-162', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Rethinking User Interfaces for Feature Location', NULL, NULL, NULL, '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Beck2015', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000311', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Beckhaus2010', 'Researchers in the IS domain have addressed communication structure and its effect on performance. While early research focused on small networks and utilized sociometric surveys, recent works have concentrated on electronic data sources provided by open source software repositories. Surprisingly, software vendors have less frequently been studied despite their need for continuous enhancement of organizational design. In this study, we analyze a global business software vendor by utilizing existing data sources. We investigate the association of both collaboration network centrality and communication with process efficiency. Despite coping with complex, interweaved processes and social networks, we find that communication, centrality in the large case-study-wide network, and communication pattern homogeneity are positively associated with process efficiency. However, centrality in small work groups slows the analyzed issue tracking process down, possibly due to increased overhead and bottleneck effects that become visible when looking at single issues qualitatively.', NULL, NULL, 'A. Beckhaus and L. M. Karg and D. Neumann', 'System Sciences (HICSS), 2010 43rd Hawaii International Conference on', NULL, NULL, NULL, '10.1109/HICSS.2010.381', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'business data processing;groupware;information systems;public domain software;software houses;business software vendor;collaboration network structure;communication pattern homogeneity;electronic data sources;information system;issue tracking process;large business software vendor;open source software repositories;social networks;Business;Collaborative software;Collaborative work;Computer industry;Manufacturing industries;Open source software;Programming;Social network services;Software engineering;Software performance', NULL, NULL, 'Jan', NULL, NULL, NULL, '1-10', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The Impact of Collaboration Network Structure on Issue Tracking''s Process Efficiency at a Large Business Software Vendor', NULL, NULL, NULL, '2010', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Beckhaus2010', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000312', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Behl2014', 'Bug report contains a vital role during software development, However bug reports belongs to different categories such as performance, usability, security etc. This paper focuses on security bug and presents a bug mining system for the identification of security and non-security bugs using the term frequency-inverse document frequency (TF-IDF) weights and naïve bayes. We performed experiments on bug report repositories of bug tracking systems such as bugzilla and debugger. In the proposed approach we apply text mining methodology and TF-IDF on the existing historic bug report database based on the bug s description to predict the nature of the bug and to train a statistical model for manually mislabeled bug reports present in the database. The tool helps in deciding the priorities of the incoming bugs depending on the category of the bugs i.e. whether it is a security bug report or a non-security bug report, using naïve bayes. Our evaluation shows that our tool using TF-IDF is giving better results than the naïve bayes method. © 2014 IEEE.', NULL, NULL, 'Behl, D. and Handa, S. and Arora, A.', NULL, NULL, NULL, NULL, '10.1109/ICROIT.2014.6798341', NULL, NULL, NULL, NULL, NULL, NULL, 'ICROIT 2014 - Proceedings of the 2014 International Conference on Reliability, Optimization and Information Technology', NULL, NULL, NULL, NULL, NULL, 'cited By 1', NULL, NULL, '294-299', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'A bug Mining tool to identify and analyze security bugs using Naive Bayes and TF-IDF: A Comparative Analysis', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84900319880&partnerID=40&md5=ab7d68ceb18934d44e5ff6ba3b226ed8', NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Behl2014', '1');
