INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000314', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Bettenburg:2007:QBR:1328279.1328284', NULL, 'New York, NY, USA', NULL, 'Bettenburg, Nicolas and Just, Sascha and Schr\\\"{o}ter, Adrian and Wei\\ss, Cathrin and Premraj, Rahul and Zimmermann, Thomas', 'Proceedings of the 2007 OOPSLA Workshop on Eclipse Technology eXchange', NULL, NULL, NULL, '10.1145/1328279.1328284', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Montreal, Quebec, Canada', NULL, NULL, NULL, NULL, '21--25', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'eclipse ''07', 'Quality of Bug Reports in Eclipse', NULL, 'http://doi.acm.org/10.1145/1328279.1328284', NULL, '2007', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Bettenburg:2007:QBR:1328279.1328284', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000316', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Bettenburg2008a', 'In software development, bug reports provide crucial information to developers. However, these reports widely differ in their quality. We conducted a survey among developers and users of APACHE, ECLIPSE, and MOZILLA to find out what makes a good bug report. The analysis of the 466 responses revealed an information mismatch between what developers need and what users supply. Most developers consider steps to reproduce, stack traces, and test cases as helpful, which are at the same time most difficult to provide for users. Such insight is helpful to design new bug tracking tools that guide users at collecting and providing more helpful information. Our CUEZILLA prototype is such a tool and measures the quality of new bug reports; it also recommends which elements should be added to improve the quality. We trained CUEZILLA on a sample of 289 bug reports, rated by developers as part of the survey. In our experiments, CUEZILLA was able to predict the quality of 31 - 48% of bug reports accurately. &copy; 2008 ACM.', 'Atlanta, GA, United states', NULL, 'Bettenburg, Nicolas and Just, Sascha and Schroter, Adrian and Weiss, Cathrin and Premraj, Rahul and Zimmermann, Thomas', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Proceedings of the ACM SIGSOFT Symposium on the Foundations of Software Engineering', 'Program debugging', 'Computer software selection and evaluation;Software engineering;Surveys;', 'English', NULL, NULL, 'Bug reports;Bug tracking;Mozilla;Software development;Test case;', NULL, NULL, '308 - 318', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'What makes a good bug report?', NULL, 'http://dx.doi.org/10.1145/1453101.1453146', NULL, '2008', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:6]', 'Bettenburg2008a', '1');
