INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000319', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Bissyande:2013:OSP:2549855.2549894', NULL, 'Washington, DC, USA', NULL, 'Bissyand{\\''e}, Tegawend{\\''e} F. and Thung, Ferdian and Lo, David and Jiang, Lingxiao and R{\\''e}veill\\\`{e}re, Laurent', 'Proceedings of the 2013 18th International Conference on Engineering of Complex Computer Systems', NULL, NULL, NULL, '10.1109/ICECCS.2013.42', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Project Search, Orion, GitHub, DSL', NULL, NULL, NULL, NULL, NULL, NULL, '242--245', NULL, NULL, NULL, NULL, 'IEEE Computer Society', NULL, NULL, NULL, NULL, NULL, 'ICECCS ''13', 'Orion: A Software Project Search Engine with Integrated Diverse Software Artifacts', NULL, 'http://dx.doi.org/10.1109/ICECCS.2013.42', NULL, '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Bissyande:2013:OSP:2549855.2549894', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000320', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Bissyande2013', 'To collect software bugs found by users, development teams often set up bug trackers using systems such as Bugzilla. Developers would then fix some of the bugs and commit corresponding code changes into version control systems such as svn or git. Unfortunately, the links between bug reports and code changes are missing for many software projects as the bug tracking and version control systems are often maintained separately. Yet, linking bug reports to fix commits is important as it could shed light into the nature of bug fixing processes and expose patterns in software management. Bug linking solutions, such as ReLink, have been proposed. The demonstration of their effectiveness however faces a number of issues, including a reliability issue with their ground truth datasets as well as the extent of their measurements. We propose in this study a benchmark for evaluating bug linking solutions. This benchmark includes a dataset of about 12,000 bug links from 10 programs. These true links between bug reports and their fixes have been provided during bug fixing processes. We designed a number of research questions, to assess both quantitatively and qualitatively the effectiveness of a bug linking tool. Finally, we apply this benchmark on ReLink to report the strengths and limitations of this bug linking tool. © 2013 IEEE.', NULL, NULL, 'Bissyandé, T.F.a and Thung, F.b a