INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000369', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Felderer2015', 'Systematic defect management based on bug-tracking systems such as Bugzilla is well established and has been successfully used in many software organizations. Defect management weights the failures observed during test execution according to their severity and forms the basis for effective defect taxonomies. In practice, most defect taxonomies are used only for the a posteriori allocation of testing resources to prioritize failures for debugging. Thus, these taxonomies'' full potential to control and improve all the steps of testing has remained unexploited. This is especially the case for testing a system''s user requirements. System-level defect taxonomies can improve the design of requirements-based tests, the tracing of defects to requirements, the quality assessment of requirements, and the control of the relevant defect management. So, we developed requirements-based testing with defect taxonomies (RTDT). This approach is aligned with the standard test process and uses defect taxonomies to support all phases of testing requirements. To illustrate this approach and its benefits, we use an example project (which we call Project A) from a public health insurance institution.', NULL, NULL, 'M. Felderer and A. Beer', NULL, NULL, NULL, NULL, '10.1109/MS.2014.56', NULL, NULL, NULL, NULL, NULL, NULL, 'IEEE Software', NULL, 'program testing;software quality;Bugzilla;RTDT;a posteriori allocation;bug-tracking system;defect taxonomies;requirements quality assessment;requirements-based testing;systematic defect management;Graphical user interfaces;Requirements engineering;Software engineering;Software testing;Syntactics;Taxonomy;defect taxonomy;requirements validation;requirements-based testing;software engineering;software quality;test management', NULL, NULL, 'May', NULL, '3', NULL, '94-101', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Using Defect Taxonomies for Testing Requirements', NULL, NULL, '32', '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Felderer2015', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000370', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Feng2013', 'Most of large web-based development communities require a bug tracking system to keep track of various bug reports. However, duplicate bug reports tend to result in waste of resources, and may cause potential conflicts. There have been two types of works focusing on this problem: relevant bug report retrieval [8][11][10][13] and duplicate bug report identification [5][12]. The former methods can achieve high accuracy (82%) in the top 10 results in some dataset, but they do not really reduce the workload of developers. The latter methods still need further improvement on the performance. In this paper, we propose a practical duplicate bug reports detection method, which aims to help project team to reduce their workload by combining existing two categories of methods. We also propose some new features extracted from comments, user profiles and query feedback, which are useful for improving the detection performance. Experiments on real dataset show that our method improves the accuracy rate by 23% compared to state-of-the-art work in duplicate bug report identification, and improves the recall rate by up to 8% in relevant bug report retrieval. © 2013 Springer-Verlag.', NULL, NULL, 'Feng, L.a and Song, L.a and Sha, C.b and Gong, X.a ', NULL, NULL, NULL, NULL, '10.1007/978-3-642-37401-2_69', NULL, NULL, NULL, NULL, NULL, NULL, 'Lecture Notes in Computer Science (including subseries Lecture Notes in Artificial Intelligence and Lecture Notes in Bioinformatics)', NULL, NULL, NULL, NULL, NULL, 'cited By 0', NULL, NULL, '709-720', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Practical duplicate bug reports detection in a large web-based development community', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84875826407&partnerID=40&md5=3f8fd101894173ea1031268c5865fb36', '7808 LNCS', '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Feng2013', '1');
