INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000381', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Gonzalez-Barahona2015', 'The Metrics Grimoire system is composed by a set of tools designed to retrieve data from repositories related to software development. Their aim is to produce organized databases suitable for easy querying with research and industrial purposes. The data in those databases have a similar structure, to easy cross-database studies, and can be enriched with information such as linkage of the multiple identities of actors, or their affiliation. This paper presents the general structure of those databases, and a collection of up-to-date database dumps that are publicly available. They correspond to two well-known projects, Open Stack, and Eclipse, including data from source code management repositories, issue tracking systems, mailing lists, and code review systems.', NULL, NULL, 'J. M. Gonzalez-Barahona and G. Robles and D. Izquierdo-Cortazar', '2015 IEEE/ACM 12th Working Conference on Mining Software Repositories', NULL, NULL, NULL, '10.1109/MSR.2015.68', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'database management systems;software engineering;Eclipse;Open Stack;metrics grimoire database collection;software development;source code management repositories;up-to-date database;Companies;Databases;Merging;Open source software;Standards organizations', NULL, NULL, 'May', NULL, NULL, NULL, '478-481', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The MetricsGrimoire Database Collection', NULL, NULL, NULL, '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Gonzalez-Barahona2015', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000383', (SELECT entry_types_id FROM entry_types WHERE label='book'), 'Gunderloy2005', 'A very well put together manual about how and why things work the way they do within FogBugz 4.0. - Matt Hawley, eWorld.UI This book is an almost perfect introduction and end user manual for FogBugz version 4. - Dean Wilson, London.pm I recommend this book if you do plan to use FogBugz. - Greg Robinson''s Blog & well written and easy to get through & - .farshid sedghi Blog I quickly realized that FogBugz could single-handedly manage my support requests, sales requests, bug tracking and project management for CodeSmith. I bought my copy about a week ago and I could not be more happy with my purchase! - Eric J. Smith''s Weblog Many programs purport to help a development team manage a projectbut most of them aren''t very good. Enter FogBugz. This dynamic tool is based on keeping track of a database of cases. At any given time, cases are assigned to one person, who must resolve or forward them to someone else. With FogBugz, cases can be prioritized, documented, sorted, discussed, edited, assigned, estimated, searched, and tracked. And because FogBugz is web-based, everyone on a development team has access to the whole picture, at any given moment. That picture may include everything from customer feature requests, to high-level design discussions, to tiny bug fix details. This book (written under the guidance of the entire FogBugz team) completely describes the ins and outs of the latest version of FogBugz. Copyright © 2005 by Mike Gunderloy. All rights reserved.', NULL, NULL, 'Gunderloy, M.', NULL, NULL, NULL, NULL, '10.1007/978-1-4302-0008-6', NULL, NULL, NULL, NULL, NULL, NULL, 'Painless Project Management with FogBugz', NULL, NULL, NULL, NULL, NULL, 'cited By 0', NULL, NULL, '1-184', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Painless project management with FogBugz', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84892242200&partnerID=40&md5=e490d1477155b0ba1db73d2b9583ad4c', NULL, '2005', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Gunderloy2005', '1');
