INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000403', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Izquierdo2015', 'Reporting bugs, asking for new features and in general giving any kind of feedback is a common way to contribute to an Open-Source Software (OSS) project. In GitHub, the largest code hosting service for OSS, this feedback is typically expressed as new issues for the project managed by an issue-tracking system available in each new project repository. Among other features, the issue tracker allows creating and assigning labels to issues with the goal of helping the project community to better classify and manage those issues (e.g., facilitating the identification of issues for top priority components or candidate developers that could solve them). Nevertheless, as the project grows a manual browsing of the project issues is no longer feasible. In this paper we present GiLA, a tool which generates a set of visualizations to facilitate the analysis of issues in a project depending on their label-based categorization. We believe our visualizations are useful to see the most popular labels (and their relationships) in a project, identify the most active community members for those labels and compare the typical issue evolution for each label category.', NULL, NULL, 'J. L. C. Izquierdo and V. Cosentino and B. Rolandi and A. Bergel and J. Cabot', '2015 IEEE 22nd International Conference on Software Analysis, Evolution, and Reengineering (SANER)', NULL, NULL, NULL, '10.1109/SANER.2015.7081860', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program visualisation;project management;public domain software;software development management;source code (software);GiLA;GitHub label analyzer;OSS project;active community members;code hosting service;issue tracker;issue-tracking system;label-based categorization;open-source software project;project community;project management;project repository;visualizations;Communities;Computer architecture;Databases;Servers;Visualization;Web services', NULL, NULL, 'March', NULL, NULL, NULL, '479-483', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'GiLA: GitHub label analyzer', NULL, NULL, NULL, '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Izquierdo2015', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000405', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Jalbert2008a', 'Bug tracking systems are important tools that guide the maintenance activities of software developers. The utility of these systems is hampered by an excessive number of duplicate bug reports-in some projects as many as a quarter of all reports are duplicates. Developers must manually identify duplicate bug reports, but this identification process is time-consuming and exacerbates the already high cost of software maintenance. We propose a system that automatically classifies duplicate bug reports as they arrive to save developer time. This system uses surface features, textual semantics, and graph clustering to predict duplicate status. Using a dataset of 29,000 bug reports from the Mozilla project, we perform experiments that include a simulation of a real-time bug reporting environment. Our system is able to reduce development cost by filtering out 8% of duplicate bug reports while allowing at least one report for each real defect to reach developers. © 2008 IEEE.', NULL, NULL, 'Jalbert, N. and Weimer, W.', NULL, NULL, NULL, NULL, '10.1109/DSN.2008.4630070', NULL, NULL, NULL, NULL, NULL, NULL, 'Proceedings of the International Conference on Dependable Systems and Networks', NULL, NULL, NULL, NULL, NULL, 'cited By 70', NULL, NULL, '52-61', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Automated duplicate detection for bug tracking systems', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-53349154094&partnerID=40&md5=f433542303407393fb9d35ea432f497c', NULL, '2008', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Jalbert2008a', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000406', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Jeong:2009:IBT:1595696.1595715', NULL, 'New York, NY, USA', NULL, 'Jeong, Gaeul and Kim, Sunghun and Zimmermann, Thomas', 'Proceedings of the the 7th Joint Meeting of the European Software Engineering Conference and the ACM SIGSOFT Symposium on The Foundations of Software Engineering', NULL, NULL, NULL, '10.1145/1595696.1595715', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'bug report assignment, bug tossing, bug triage, issue tracking, machine learning, problem tracking', NULL, 'Amsterdam, The Netherlands', NULL, NULL, NULL, NULL, '111--120', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'ESEC/FSE ''09', 'Improving Bug Triage with Bug Tossing Graphs', NULL, 'http://doi.acm.org/10.1145/1595696.1595715', NULL, '2009', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Jeong:2009:IBT:1595696.1595715', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000408', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Johnson2003', 'All programming projects have one thing in common: there is always more to do. Some things that need doing are bug fixes; others are enhancements such as cleaning up and refactorlng existing code, adding tests, and writing documentation. but before your office wall becomes a collage of sticky-note reminders, you might want to try an issue tracker instead. In this article, we describe two open-source issue-tracking software packages: Roundup, an implementation of the winning design in Los Alamos National Laboratory''s Software Carpentry contest and Bugzilla (from the GNU project).', NULL, NULL, 'J. N. Johnson and P. F. Dubois', NULL, NULL, NULL, NULL, '10.1109/MCISE.2003.1238707', NULL, NULL, NULL, NULL, NULL, NULL, 'Computing in Science Engineering', NULL, 'client-server systems;public domain software;software packages;Bugzilla;GNU project;Los Alamos National Laboratory;Roundup;Software Carpentry contest;issue tracker;open-source issue tracking;programming projects;software packages', NULL, NULL, 'Nov', NULL, '6', NULL, '71-77', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Issue tracking', NULL, NULL, '5', '2003', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Johnson2003', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000409', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Just2008', 'Developers typically rely on the information submitted by end-users to resolve bugs. We conducted a survey on information needs and commonly faced problems with bug reporting among several hundred developers and users of the APACHE, ECLIPSE and MOZILLA projects. In this paper, we present the results of a card sort on the 175 comments sent back to us by the responders of the survey. The card sort revealed several hurdles involved in reporting and resolving bugs, which we present in a collection of recommendations for the design of new bug tracking systems. Such systems could provide contextual assistance, reminders to add information, and most important, assistance to collect and report crucial information to developers.', NULL, NULL, 'S. Just and R. Premraj and T. Zimmermann', '2008 IEEE Symposium on Visual Languages and Human-Centric Computing', NULL, NULL, NULL, '10.1109/VLHCC.2008.4639063', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program debugging;systems analysis;tracking;APACHE project;ECLIPSE project;MOZILLA project;bug tracking systems;card sort;next generation;Cognitive science;Communication system software;Computer bugs;Feedback;Information analysis;Sorting;Spatial databases;Taxonomy;Testing', NULL, NULL, 'Sept', NULL, NULL, NULL, '82-85', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Towards the next generation of bug tracking systems', NULL, NULL, NULL, '2008', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Just2008', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000410', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Kaushik2012', 'Open source projects incorporate bug triagers to help with the task of bug report assignment to developers. One of the tasks of a triager is to identify whether an incoming bug report is a duplicate of a pre-existing report. In order to detect duplicate bug reports, a triager either relies on his memory and experience or on the search capabilities of the bug repository. Both these approaches can be time consuming for the triager and may also lead to the misidentification of duplicates. In the literature, several approaches to automate duplicate bug report detection have been proposed. However, there has not been an exhaustive comparison of the performance of different IR models, especially with topic-based ones such as LSI and LDA. In this paper, we compare the performance of the traditional vector space model (using different weighting schemes) with that of topic based models, leveraging heuristics that incorporate exception stack frames, surface features, summary and long description from the free-form text in the bug report. We perform experiments on subsets of bug reports from Eclipse and Firefox and achieve a recall rate of 60% and 58% respectively. We find that word-based models, in particular a Log-Entropy based weighting scheme, outperform topic based ones such as LSI, LDA and Random Projections. Our findings also suggests that for the problem of duplicate bug detection, it is important to consider a project''s domain and characteristics to devise a set of heuristics to achieve optimal results.', NULL, NULL, 'N. Kaushik and L. Tahvildari', 'Software Maintenance and Reengineering (CSMR), 2012 16th European Conference on', NULL, NULL, NULL, '10.1109/CSMR.2012.78', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'information retrieval;program debugging;public domain software;report generators;Eclipse;Firefox;IR models;LDA;LSI;Log-Entropy based weighting scheme;bug report assignment;bug repository;bug triagers;duplicate bug detection;duplicate bug report detection automation;free-form text;open source projects;random projections;topic based models;vector space model;word-based models;Computer bugs;Entropy;Fires;Large scale integration;Software;Synchronization;Vectors;bug;duplicate;information retrieval', NULL, NULL, 'March', NULL, NULL, NULL, '159-168', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'A Comparative Study of the Performance of IR Models on Duplicate Bug Detection', NULL, NULL, NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Kaushik2012', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000411', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Keivanloo2012', 'The mining of software repositories involves the extraction of both basic and value-added information from existing software repositories. The repositories will be mined to extract facts by different stakeholders (e.g. researchers, managers) and for various purposes. To avoid unnecessary pre-processing and analysis steps, sharing and integration of both basic and value-added facts are needed. In this research, we introduce SeCold, an open and collaborative platform for sharing software datasets. SeCold provides the first online software ecosystem Linked Data platform that supports data extraction and on-the-fly inter-dataset integration from major version control, issue tracking, and quality evaluation systems. In its first release, the dataset contains about two billion facts, such as source code statements, software licenses, and code clones from 18 000 software projects. In its second release the SeCold project will contain additional facts mined from issue trackers and versioning systems. Our approach is based on the same fundamental principle as Wikipedia: researchers and tool developers share analysis results obtained from their tools by publishing them as part of the SeCold portal and therefore make them an integrated part of the global knowledge domain. The SeCold project is an official member of the Linked Data dataset cloud and is currently the eighth largest online dataset available on the Web.', NULL, NULL, 'I. Keivanloo and C. Forbes and A. Hmood and M. Erfani and C. Neal and G. Peristerakis and J. Rilling', 'Mining Software Repositories (MSR), 2012 9th IEEE Working Conference on', NULL, NULL, NULL, '10.1109/MSR.2012.6224296', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'data mining;software packages;code clones;collaborative platform;linked data platform;mining software repositories;on-the-fly inter-dataset integration;online software ecosystem linked data platform;software datasets;software licenses;software packages;software repositories;source code statements;value added information;Cloning;Communities;Data mining;Encyclopedias;Licenses;Ontologies;Software;Linked Data;fact sharing;software mining', NULL, NULL, 'June', NULL, NULL, NULL, '32-35', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'A Linked Data platform for mining software repositories', NULL, NULL, NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Keivanloo2012', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000412', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Ki2009', 'As software systems become larger and more complex, automated software engineering tools play a crucial role for effective software development management, which is a key factor to lead quality software systems. In this work, we present TRICA, an open source-based software development infrastructure. The name of TRICA represents its features such as Traceability, Relationship, Informativeness, Cost-effectiveness, and Automation. Essentially, in TRICA, a continuous integration tool is coupled with a software configuration management tool and an issue tracking tool. We provisioned a mechanism to connect the open source tools in TRICA so that project members use the collaborated information to solve various issues and implementation problems efficiently, and easily share forthcoming issues during the course of the project. We show that TRICA can help to decentralize risks throughout the software development cycle and achieve successful software development.', NULL, NULL, 'Y. Ki and M. Song', 'Automated Software Engineering, 2009. ASE ''09. 24th IEEE/ACM International Conference on', NULL, NULL, NULL, '10.1109/ASE.2009.73', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'configuration management;groupware;integrated software;program diagnostics;public domain software;software development management;software quality;software tools;TRICA;automated software engineering tools;collaborated information;continuous integration tool;open source tools;software configuration management tool;software development cycle;software development management;software quality;software systems;tracking tool;Automation;Collaborative software;Collaborative tools;Open source software;Programming;Software development management;Software engineering;Software systems;Software tools;Testing;SCM;continuous integration;issue tracking;open source;software engineering tools', NULL, NULL, 'Nov', NULL, NULL, NULL, '525-529', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'An Open Source-Based Approach to Software Development Infrastructures', NULL, NULL, NULL, '2009', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Ki2009', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000413', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Klein:2014:NFD:2597073.2597090', NULL, 'New York, NY, USA', NULL, 'Klein, Nathan and Corley, Christopher S. and Kraft, Nicholas A.', 'Proceedings of the 11th Working Conference on Mining Software Repositories', NULL, NULL, NULL, '10.1145/2597073.2597090', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Duplicate bug reports, machine learning, topic model', NULL, 'Hyderabad, India', NULL, NULL, NULL, NULL, '324--327', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'MSR 2014', 'New Features for Duplicate Bug Detection', NULL, 'http://doi.acm.org/10.1145/2597073.2597090', NULL, '2014', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Klein:2014:NFD:2597073.2597090', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000414', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Knab2009', 'Issue tracking repositories contain a wealth of information for reasoning about various aspects of software development processes. In this paper, we focus on bug triaging and provide visual means to explore the effort estimation quality and the bug life-cycle of reported problems. Our approach follows the micro/macro reading technique and uses a combination of graphical views to investigate details of individual problem reports while maintaining the context provided by the surrounding data population. This enables the detection and detailed analysis of hidden patterns and facilitates the analysis of problem report outliers. In an industrial study, we use our approach in various problem report analysis scenarios and answer questions related to effort estimation and resource planning.', NULL, NULL, 'P. Knab and B. Fluri and H. C. Gall and M. Pinzger', 'Software Maintenance, 2009. ICSM 2009. IEEE International Conference on', NULL, NULL, NULL, '10.1109/ICSM.2009.5306384', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'data visualisation;program debugging;bug life-cycle;bug triaging;macroreading technique;microreading technique;problem report;software development;Life estimation;Pattern analysis;Programming', NULL, NULL, 'Sept', NULL, NULL, NULL, '527-530', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Interactive views for analyzing problem reports', NULL, NULL, NULL, '2009', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Knab2009', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000415', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Knab:2009:SVA:1595696.1595746', NULL, 'New York, NY, USA', NULL, 'Knab, Patrick and Knab, Patrick and Pinzger, Martin', 'Proceedings of the the 7th Joint Meeting of the European Software Engineering Conference and the ACM SIGSOFT Symposium on The Foundations of Software Engineering', NULL, NULL, NULL, '10.1145/1595696.1595746', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'effort estimation, process metrics, visualization', NULL, 'Amsterdam, The Netherlands', NULL, NULL, NULL, NULL, '289--290', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'ESEC/FSE ''09', 'Smart Views for Analyzing Problem Reports: Tool Demo', NULL, 'http://doi.acm.org/10.1145/1595696.1595746', NULL, '2009', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Knab:2009:SVA:1595696.1595746', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000416', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Knauss2012', 'In current project environments, requirements often evolve throughout the project and are worked on by stakeholders in large and distributed teams. Such teams often use online tools such as mailing lists, bug tracking systems or online discussion forums to communicate, clarify or coordinate work on requirements. In this kind of environment, the expected evolution from initial idea, through clarification, to a stable requirement, often stagnates. When project managers are not aware of underlying problems, development may proceed before requirements are fully understood and stabilized, leading to numerous implementation issues and often resulting in the need for early redesign and modification. In this paper, we present an approach to analyzing online requirements communication and a method for the detection and classification of clarification events in requirement discussions. We used our approach to analyze online requirements communication in the IBM® Rational Team Concert® (RTC) project and identified a set of six clarification patterns. Since a predominant amount of clarifications through the lifetime of a requirement is often indicative of problematic requirements, our approach lends support to project managers to assess, in real-time, the state of discussions around a requirement and promptly react to requirements problems.', NULL, NULL, 'E. Knauss and D. Damia