INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000422', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Kulkarni2014', 'This paper addresses the problem of “deep matching” - or matching different classes of entities based on latent underlying semantics, rather than just their visible attributes. An example of this is the “automatic task assignment” problem where several tasks have to be assigned to people with varied skill-sets and experiences. Datasets showing types of entities (tasks and people) along with their involvement of other concepts, are used as the basis for deep matching. This paper describes a work in progress, of a deep matching application called SortingHat. We analyze issue tracking data of a large corporation containing task descriptions and assignments to people that were computed manually. We identify several entities and concepts from the dataset and build a co-occurrence graph as the basic data structure for computing deep matches. We then propose a set of query primitives that can establish several forms of semantic matching across different classes of entities.', NULL, NULL, 'S. Kulkarni and S. Srinivasa and J. N. Khasnabish and K. Nagal and S. G. Kurdagi', 'Data Engineering Workshops (ICDEW), 2014 IEEE 30th International Conference on', NULL, NULL, NULL, '10.1109/ICDEW.2014.6818309', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'data analysis;data structures;graph theory;pattern matching;SortingHat;automatic task assignment problem;co-occurrence graph;data structure;deep matching problem;entity classes;query primitives;semantic matching;tracking data analysis;visible attributes;Cities and towns;Conferences;Data mining;Information technology;Internet;Probability distribution;Semantics', NULL, NULL, 'March', NULL, NULL, NULL, '90-93', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'SortingHat: A framework for deep matching between classes of entities', NULL, NULL, NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Kulkarni2014', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000424', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Lal2012', 'Bug reports submitted to an issue tracking system can belong to different categories such as crash, regression, security, cleanup, polish, performance and usability. A deeper understanding of the properties and features of various categories of bug reports can have implications in improving software maintenance processes, tools and practices. We identify several metrics and characteristics serving as dimensions on which various types of bug reports can be compared. We perform a case-study on Google Chromium Browser open-source project and conduct a series of experiments to calculate various metrics. We present a characterization study comparing different types of bug reports on metrics such as: statistics on close-time, number of stars, number of comments, discriminatory and frequent words for each class, entropy across reporters, entropy across component, opening and closing trend, continuity and debugging efficiency performance characteristics. The calculated metrics shows the similarities and differences on various dimensions for seven different types of bug reports.', NULL, NULL, 'S. Lal and A. Sureka', '2012 19th Asia-Pacific Software Engineering Conference', NULL, NULL, NULL, '10.1109/APSEC.2012.54', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'online front-ends;program debugging;software maintenance;software metrics;Google Chrome browser project;Google Chromium browser open-source project;bug report types;debugging efficiency performance characteristics;issue tracking system;software maintenance processes;software metrics;Computer bugs;Entropy;Google;Market research;Security;Usability;Issue Tracking Systems;Mining Bug Archives;Mining Software Repositories;Software Maintenance', NULL, NULL, 'Dec', NULL, NULL, NULL, '517-526', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Comparison of Seven Bug Report Types: A Case-Study of Google Chrome Browser Project', NULL, NULL, '1', '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Lal2012', '1');
