INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000430', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Lin2009', 'Bug assignment is an important step in bug life-cycle management. In large projects, this task would consume a substantial amount of human effort. To compare with the previous studies on automatic bug assignment in FOSS (free/open source software) projects, we conduct a case study on a proprietary software project in China. Our study consists of two experiments of automatic bug assignment, using Chinese text and the other non-text information of bug data respectively. Based on text data of the bug repository, the first experiment uses SVM to predict bug assignments and achieve accuracy close to that by human triagers. The second one explores the usefulness of non-text data in making such prediction. The main results from our study includes that text data are most useful data in the bug tracking system to triage bugs, and automation based on text data could effectively reduce the manual effort.', NULL, NULL, 'Z. Lin and F. Shu and Y. Yang and C. Hu and Q. Wang', '2009 3rd International Symposium on Empirical Software Engineering and Measurement', NULL, NULL, NULL, '10.1109/ESEM.2009.5315994', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program debugging;public domain software;software maintenance;statistical analysis;support vector machines;text analysis;Chinese bug textual data;FOSS;SVM;automatic bug assignment;bug life-cycle management;bug repository;empirical study;free/open source software project;triage bug tracking system;Automation;Computer bugs;Data mining;Engineering management;Humans;Open source software;Software debugging;Software engineering;Software measurement;Support vector machines', NULL, NULL, 'Oct', NULL, NULL, NULL, '451-455', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'An empirical study on bug assignment automation using Chinese bug data', NULL, NULL, NULL, '2009', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Lin2009', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000431', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Liu2005', 'When teaching communication and teamwork skills in software engineering courses, it is often difficult to relate the theories of communication as presented in communication textbooks to actual student interactions and team activities because the majority of student interactions and team activities take place outside the classroom. Through our experience in teaching communication theories in CS456/556, a software engineering course at Ohio University, we observed that when communication theories are delivered in traditional methods such as lectures without additional exercises designed for students to apply the theories, many students tend to treat them as an independent part of the course and continue to guide their behaviors in team activities with their old habits and preexisting intuitions. We found that issue tracking tools can help facilitate student learning of communication skills by forcing students to explicitly carry out effective steps recommended by communication theories and thus improve communications among students. Moreover, issue tracking tools also improve communications between the students and the instructor, and enable the instructor to be more aware of team status, detect team problems early on, and reply less on time-consuming and often inaccurate in-class team status reports', NULL, NULL, 'C. Liu', '18th Conference on Software Engineering Education Training (CSEET''05)', NULL, NULL, NULL, '10.1109/CSEET.2005.40', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'behavioural sciences;computer science education;educational courses;software engineering;teaching;team working;Ohio University;communication skills teaching;issue tracking tools;lectures;software engineering courses;student behavior;student exercises;student interactions;student learning;team activities;team problems;team status;teamwork skills teaching;Computer science;Education;Educational institutions;Feedback;Programming;Software design;Software engineering;Software tools;Teamwork;Unified modeling language', NULL, NULL, 'April', NULL, NULL, NULL, '61-68', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Using Issue Tracking Tools to Facilitate Student Learning of Communication Skills in Software Engineering Courses', NULL, NULL, NULL, '2005', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Liu2005', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000432', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Liu2014', 'During bug reporting, The same bugs could be repeatedly reported. As a result, extra time could be spent on bug triaging and fixing. In order to reduce redundant effort, it is important to provide bug reporters with the ability to search for previously reported bugs efficiently and accurately. The existing bug tracking systems are using relatively simple ranking functions, which often produce unsatisfactory results. In this paper, we apply Ranking SVM, a Learning to Rank technique to construct a ranking model for accurate bug report search. Based on the search results, a topic model is used to cluster the bug reports into multiple facets. Each facet contains similar bug reports of the same topic. Users and testers can locate relevant bugs more efficiently through a simple query. We perform evaluations on more than 16,340 Eclipse and Mozilla bug reports. The evaluation results show that the proposed approach can achieve better search results than the existing search functions.', NULL, NULL, 'K. Liu and H. B. K. Tan', 'Computer Software and Applications Conference (COMPSAC), 2014 IEEE 38th Annual', NULL, NULL, NULL, '10.1109/COMPSAC.2014.19', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'learning (artificial intelligence);program debugging;search problems;support vector machines;Eclipse bug reports;Mozilla bug reports;bug tracking systems;faceted bug report search;learning;ranking SVM;simple ranking functions;topic model;Computational modeling;Databases;Feature extraction;Standards;Support vector machines;Training;Vectors;bug report search;clustering;faceted search;ranking SVM;topic model', NULL, NULL, 'July', NULL, NULL, NULL, '123-128', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Faceted Bug Report Search with Topic Model', NULL, NULL, NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Liu2014', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000433', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Liu:2012:TBR:2393596.2393628', NULL, 'New York, NY, USA', NULL, 'Liu, Kaiping and Tan, Hee Beng Kuan and Chandramohan, Mahinthan', 'Proceedings of the ACM SIGSOFT 20th International Symposium on the Foundations of Software Engineering', NULL, NULL, NULL, '10.1145/2393596.2393628', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'bug report, bug tracking system, duplicate, learning to rank, search engine, search quality', NULL, 'Cary, North Carolina', NULL, NULL, NULL, NULL, '28:1--28:4', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'FSE ''12', 'Has This Bug Been Reported?', NULL, 'http://doi.acm.org/10.1145/2393596.2393628', NULL, '2012', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Liu:2012:TBR:2393596.2393628', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000435', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Lotufo2012', 'Low bug report quality and human conflicts pose challenges to keep bug tracking systems productive. This work proposes to address these issues by applying game mechanisms to bug tracking systems. We investigate the use of game mechanisms in Stack Overflow, an online community organized to resolve computer programming related problems, for which the improvements we seek for bug tracking systems also turn out to be relevant. The results of our Stack Overflow investigation show that its game mechanisms could be used to address these issues by motivating contributors to increase contribution frequency and quality, by filtering useful contributions, and by creating an agile and dependable moderation system. We proceed by mapping these mechanisms to open-source bug tracking systems, and find that most benefits are applicable. Additionally, our results motivate tailoring a reward and reputation system and summarizing bug reports as future directions for increasing the benefits of game mechanisms in bug tracking systems. © 2012 IEEE.', NULL, NULL, 'Lotufo, R. and Passos, L. and Czarnecki, K.', NULL, NULL, NULL, NULL, '10.1109/MSR.2012.6224293', NULL, NULL, NULL, NULL, NULL, NULL, 'IEEE International Working Conference on Mining Software Repositories', NULL, NULL, NULL, NULL, NULL, 'cited By 0', NULL, NULL, '2-11', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Towards improving bug tracking systems with game mechanisms', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84865142761&partnerID=40&md5=001e3ff8659af56ee87a377095221940', NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Lotufo2012', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000436', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Lotufo2012a', 'Low bug report quality and human conflicts pose challenges to keep bug tracking systems productive. This work proposes to address these issues by applying game mechanisms to bug tracking systems. We investigate the use of game mechanisms in Stack Overflow, an online community organized to resolve computer programming related problems, for which the improvements we seek for bug tracking systems also turn out to be relevant. The results of our Stack Overflow investigation show that its game mechanisms could be used to address these issues by motivating contributors to increase contribution frequency and quality, by filtering useful contributions, and by creating an agile and dependable moderation system. We proceed by mapping these mechanisms to open-source bug tracking systems, and find that most benefits are applicable. Additionally, our results motivate tailoring a reward and reputation system and summarizing bug reports as future directions for increasing the benefits of game mechanisms in bug tracking systems. &copy; 2012 IEEE.', 'Zurich, Switzerland', NULL, 'Lotufo, Rafael and Passos, Leonardo and Czarnecki, Krzysztof', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'IEEE International Working Conference on Mining Software Repositories', 'Tracking (position)', 'Computer programming;', 'English', NULL, NULL, 'Bug reports;Bug tracking system;Online communities;Open-source;Reputation systems;Stack overflow;', NULL, NULL, '2 - 11', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Towards improving bug tracking systems with game mechanisms', NULL, 'http://dx.doi.org/10.1109/MSR.2012.6224293', NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:6]', 'Lotufo2012a', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000437', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Malheiros2012', 'Newcomers in a software development project often need assistance to complete their first tasks. Then a mentor, an experienced member of the team, usually teaches the newcomers what they need to complete their tasks. But, to allocate an experienced member of a team to teach a newcomer during a long time is neither always possible nor desirable, because the mentor could be more helpful doing more important tasks. During the development the team interacts with a version control system, bug tracking and mailing lists, and all these tools record data creating the project memory. Recommender systems can use the project memory to help newcomers in some tasks answering their questions, thus in some cases the developers do not need a mentor. In this paper we present Mentor, a recommender system to help newcomers to solve change requests. Mentor uses the Prediction by Partial Matching (PPM) algorithm and some heuristics to analyze the change requests, and the version control data, and recommend potentially relevant source code that will help the developer in the change request solution. We did three experiments to compare the PPM algorithm with the Latent Semantic Indexing (LSI). Using PPM we achieved results for recall rate between 37% and 66.8%, and using LSI the results were between 20.3% and 51.6%.', NULL, NULL, 'Y. Malheiros and A. Moraes and C. Trindade and S. Meira', '2012 IEEE 36th Annual Computer Software and Applications Conference', NULL, NULL, NULL, '10.1109/COMPSAC.2012.11', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'configuration management;indexing;pattern matching;program debugging;recommender systems;software maintenance;LSI;Mentor;PPM algorithm;bug tracking;experienced team member;latent semantic indexing;mailing lists;newcomers;prediction by partial matching algorithm;project memory;recommender system;software development project;source code recommender system;version control data;version control system;Context;Databases;Entropy;Large scale integration;Measurement;Recommender systems;Software;information theory;recommender systems;software engineering;software maintenance', NULL, NULL, 'July', NULL, NULL, NULL, '19-24', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'A Source Code Recommender System to Support Newcomers', NULL, NULL, NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Malheiros2012', '1');
