INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000439', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Mani:2013:BRC:2487085.2487124', NULL, 'Piscataway, NJ, USA', NULL, 'Mani, Senthil and Nagar, Seema and Mukherjee, Debdoot and Narayanam, Ramasuri and Sinha, Vibha Singhal and Nanavati, Amit A.', 'Proceedings of the 10th Working Conference on Mining Software Repositories', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'San Francisco, CA, USA', NULL, NULL, NULL, NULL, '193--202', NULL, NULL, NULL, NULL, 'IEEE Press', NULL, NULL, NULL, NULL, NULL, 'MSR ''13', 'Bug Resolution Catalysts: Identifying Essential Non-committers from Bug Repositories', NULL, 'http://dl.acm.org/citation.cfm?id=2487085.2487124', NULL, '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Mani:2013:BRC:2487085.2487124', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000440', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Masmoudi2013', 'There is a little understanding of distributed solving activities in Open Source communities. This study aimed to provide some insights in this way. It was applied to the context of Bugzilla, the bug tracking system of Mozilla community. This study investigated the organizational aspects of this meditated, complex and highly distributed context through a linguistic analysis method. The main finding of this research shows that the organization of distributed problem-solving activities in Bugzilla isn''t based only on the hierarchical distribution of the work between core and periphery participants but on their implication in the interactions. This implication varies according to the status of each one participant in the community. That is why we distinguish their roles, as well as, the established modes to manage such activity.', NULL, NULL, 'H. Masmoudi and I. Boughzala', 'IEEE 7th International Conference on Research Challenges in Information Science (RCIS)', NULL, NULL, NULL, '10.1109/RCIS.2013.6577739', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'distributed processing;program debugging;Bugzilla;Mozilla community;bug tracking system;distributed problem-solving;linguistic analysis method;open source community;Communities;Context;Focusing;Organizations;Pragmatics;Problem-solving;Software;Distributed resolution;Interaction;Language;Open Source Community;Organisation', NULL, NULL, 'May', NULL, NULL, NULL, '1-2', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'A case study for understanding the organization of distributed problem-solving within the Mozilla''s community: Poster paper', NULL, NULL, NULL, '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Masmoudi2013', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000441', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Masmoudi2013a', 'The emerging empirical literature on Open Source communities indicates that a majority of code writing and communication activity is concentrated with a few contributors, the “core” (maintainers). However, these communities allow and encourage participation from anybody, the “periphery”. The focus of this work is on explaining how distributed communities solve software problems through the participation of a large number of participants. In particular, this paper investigates interaction, collaboration and division of labor between the core and periphery in a distributed problem-solving activity. Using a linguistic method of analysis, we study bugs that affected Firefox Internet browser as reflected in the discussions and actions reported in Bugzilla (the Mozilla''s bug tracking system). As results, we find various categories in the modes of interaction between the core and periphery participants of the community and suggest that interactions are influenced by their status.', NULL, NULL, 'H. Masmoudi and V. Fernandez and L. Marrauld', 'Engineering, Technology and Innovation (ICE) IEEE International Technology Management Conference, 2013 International Conference on', NULL, NULL, NULL, '10.1109/ITMC.2013.7352679', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'online front-ends;problem solving;program debugging;public domain software;Bugzilla;Firefox Internet browser;Mozilla community;distributed problem-solving activity;distributed problem-solving network;linguistic method;open source community;software problem;Computer bugs;Context;Measurement;Organizations;Pragmatics;Problem-solving;Software;Collaboration;Interaction;Open Source;Problem-Solving;lexical analysis', NULL, NULL, 'June', NULL, NULL, NULL, '1-10', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The organization of distributed problem-solving networks: Examining how core and periphery interact together to solve problems in Mozilla''s community', NULL, NULL, NULL, '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Masmoudi2013a', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000442', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Matsumoto2012', 'SaaS (Software as a Service) is software that provides the necessary service only when actually required. On the other hand, PaaS (Platform as a Service) is a platform where integrated software development is executed using the networked environment. SaaS for developers is supported by version control systems or forums. However, these services do not support deployment. Therefore, users need to use other services (like PaaS). We propose a development environment service, in which development and deployment are integrated. In this paper, we propose a development and deployment service in the Educational Cloud. The integrated system is referred to as Development as a Service (DEVaaS), which describes the system. This system currently supports a bug tracking system, continuous integration system, version control system, several well-known programming languages, an editor, and deployment environments in the cloud.', NULL, NULL, 'K. Matsumoto and S. Kibe and M. Uehara and H. Mori', '2012 15th International Conference on Network-Based Information Systems', NULL, NULL, NULL, '10.1109/NBiS.2012.60', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'cloud computing;computer aided instruction;software engineering;DEVaaS;PaaS;SaaS;design;development as a service;educational cloud;integrated software development;platform as a service;software as a service;Computer languages;Control systems;Internet;Project management;Servers;Cloud;DEVaaS;Development;PaaS;SaaS', NULL, NULL, 'Sept', NULL, NULL, NULL, '815-819', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Design of Development as a Service in the Cloud', NULL, NULL, NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Matsumoto2012', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000443', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Matsushita2005', 'In typical open source software development, developers use revision control systems for product management, mailing list systems for human communications, and bug tracking systems for process management. All of these systems store development histories of the products that show significant information of problems during the development. However, it would be a hard job to retrieve useful information related to a current problem faced by developers. In this paper, we describe a software development supporting system CoxR that is capable of crawling the development histories. CoxR creates software development information Web which consists of developers, emails, and program deltas, and provides an interface to search, navigate, browse, and retrieve past development results. Through a case study, we confirmed that CoxR helps developers to solve their problems by making it easier to search development history.', NULL, NULL, 'M. Matsushita and K. Sasaki and K. Inoue', '12th Asia-Pacific Software Engineering Conference (APSEC''05)', NULL, NULL, NULL, '10.1109/APSEC.2005.56', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'public domain software;search engines;software engineering;CoxR;information retrieval;open source development history search system;software development supporting system;Communication system control;Control systems;Data mining;History;Information retrieval;Navigation;Open source software;Programming;Software development management;Technology management', NULL, NULL, 'Dec', NULL, NULL, NULL, '6 pp.-', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'CoxR: open source development history search system', NULL, NULL, NULL, '2005', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Matsushita2005', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000444', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Mausa2014', 'Empirical software engineering research community aims to accumulate knowledge in software engineering community based on the empirical studies on datasets obtained from the real software projects. Limiting factor to building the theory over thus accumulated knowledge is often related to dataset bias. One solution to this problem is developing a systematic data collection procedure through standard guidelines that would be available to open community and thus enable reducing data collection bias. In this paper we present a tool demonstration that implements a systematic data collection procedure for software defect prediction datasets from the open source bug tracking and the source code management repositories. Main challenging issue that the tool addresses is linking the information related to the same entity (e.g. class file) from these two sources. The tool implements interfaces to bug and source code repositories and even other tools for calculating the software metrics. Finally, it offers the user to create software defect prediction datasets even if he is unaware of all the details behind this complex task.', NULL, NULL, 'G. Mauša and T. G. Grbac and B. D. Bašić', 'Software, Telecommunications and Computer Networks (SoftCOM), 2014 22nd International Conference on', NULL, NULL, NULL, '10.1109/SOFTCOM.2014.7039122', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program debugging;program testing;public domain software;software metrics;source code (software);bug-code analyzer;class file;complex task;data collection bias reduction;data collection tool;empirical software engineering research community;open community;open source bug tracking;real software projects;software defect prediction datasets;software metrics;source code management repositories;standard guidelines;systematic data collection procedure;Communities;Computer bugs;Data collection;Joining processes;Measurement;Software;Software engineering;Automated Tool;Mining Software Repositories;Software Defect Prediction', NULL, NULL, 'Sept', NULL, NULL, NULL, '425-426', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Software defect prediction with Bug-Code analyzer - A data collection tool demo', NULL, NULL, NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Mausa2014', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000445', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'McLaughlin2004', 'Bug tacking systems give developers a unique and clear view into user''s everyday product experiences. Adding some statistical analysis and software teams can efficiently improve product quality. It''s hard to tell precisely how well the error reporting system working, but this seems to be a bug weapon that has landed a permanent spot in microsoft''s arsenal. Automated bug tracking, combined with statistical reporting, plays a key role for developers at the Mozilla Foundations, best known for its open source Web browser and email software. The sparse, random sampling approach produces enough data for the team to do what it call \"statistical debugging\"-bug detection through statistical analysis.', NULL, NULL, 'L. McLaughlin', NULL, NULL, NULL, NULL, '10.1109/MS.2004.1259245', NULL, NULL, NULL, NULL, NULL, NULL, 'IEEE Software', NULL, 'DP industry;data privacy;program debugging;software quality;software tools;statistical analysis;automated bug tracking system;data privacy;email software;open source Web browser;product experiences;product quality;statistical analysis;statistical debugging;statistical reporting', NULL, NULL, 'Jan', NULL, '1', NULL, '100-103', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Automated bug tracking: the promise and the pitfalls', NULL, NULL, '21', '2004', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'McLaughlin2004', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000446', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Menzies2008', 'In mission critical systems, such as those developed by NASA, it is very important that the test engineers properly recognize the severity of each issue they identify during testing. Proper severity assessment is essential for appropriate resource allocation and planning for fixing activities and additional testing. Severity assessment is strongly influenced by the experience of the test engineers and by the time they spend on each issue. The paper presents a new and automated method named SEVERIS (severity issue assessment), which assists the test engineer in assigning severity levels to defect reports. SEVERIS is based on standard text mining and machine learning techniques applied to existing sets of defect reports. A case study on using SEVERIS with data from NASApsilas Project and Issue Tracking System (PITS) is presented in the paper. The case study results indicate that SEVERIS is a good predictor for issue severity levels, while it is easy to use and efficient.', NULL, NULL, 'T. Menzies and A. Marcus', 'Software Maintenance, 2008. ICSM 2008. IEEE International Conference on', NULL, NULL, NULL, '10.1109/ICSM.2008.4658083', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'data mining;learning (artificial intelligence);resource allocation;software engineering;NASA;automated severity assessment;machine learning techniques;mission critical systems;resource allocation;severity issue assessment;software defect reports;text mining;Automatic testing;Computer bugs;Computer science;Costs;NASA;Personnel;Robots;Software testing;System testing;Text mining', NULL, NULL, 'Sept', NULL, NULL, NULL, '346-355', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Automated severity assessment of software defect reports', NULL, NULL, NULL, '2008', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Menzies2008', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000447', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Merten2012', 'In a two semester software engineering (SE) course at Bonn-Rhine-Sieg University students have the opportunity to actually elicit, analyze and document requirements as well as design and develop a correspondent software product in teams of approximately four. The students have to use an issue tracking software in combination with a Requirements Engineering (RE) tool to document and plan their work. Though the course starts with RE theory from elicitation via documentation and traceability, we found that the students find it difficult to combine different RE artifact types and to develop useful traces between them. In this paper we present an approach to provide feedback and give pro-active advice inside an RE tool, while the specification is created. To derive this feedback we use a knowledge base containing rules and best practices to create a requirements specification. An assistance system applies these rules to guide the user in different situations, beginning with an empty specification up to the implementation of various RE artifact types and traces between them. This paper presents the status of our knowledge-based feedback mechanism and possible extensions. In order to get primary indicators for the value of this approach we did experiments and workshops with eight students who worked with the same tool with and without the feedback system.', NULL, NULL, 'T. Merten and T. Schäfer and S. Bürsner', 'Requirements Engineering Education and Training (REET), 2012 IEEE 7th International Workshop on', NULL, NULL, NULL, '10.1109/REET.2012.6360068', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Cognition;Concrete;Documentation;Education;Knowledge based systems;Knowledge engineering;Software;RE;direct feedback;knowledge engineering;software-based feedback agents;teaching', NULL, NULL, 'Sept', NULL, NULL, NULL, '9-13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Using RE knowledge to assist automatically during requirement specification', NULL, NULL, NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Merten2012', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000448', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Michail:2005:HUA:1062455.1062490', NULL, 'New York, NY, USA', NULL, 'Michail, Amir and Xie, Tao', 'Proceedings of the 27th International Conference on Software Engineering', NULL, NULL, NULL, '10.1145/1062455.1062490', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'bug prediction, bug tracking system, fault evasion, gui applications, software testing, software tools', NULL, 'St. Louis, MO, USA', NULL, NULL, NULL, NULL, '107--116', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'ICSE ''05', 'Helping Users Avoid Bugs in GUI Applications', NULL, 'http://doi.acm.org/10.1145/1062455.1062490', NULL, '2005', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Michail:2005:HUA:1062455.1062490', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000449', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Michail2005', 'In this paper, we propose a method to help users avoid bugs in GUI applications. In particular, users would use the application normally and report bugs that they encounter to prevent anyone - including themselves - from encountering those bugs again. When a user attempts an action that has led to problems in the past, he/she will receive a warning and will be given the opportunity to abort the action thus avoiding the bug altogether and keeping the application stable. Of course, bugs should be fixed eventually by the application developers, but our approach allows application users to collaboratively help each other avoid bugs - thus making the application more usable in the meantime. We demonstrate this approach using our \"Stabilizer\" prototype. We also include a preliminary evaluation of the Stabilizer''s bug prediction. Copyright 2005 ACM.', NULL, NULL, 'Michail, A.a and Xie, T.b ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Proceedings - 27th International Conference on Software Engineering, ICSE05', NULL, NULL, NULL, NULL, NULL, 'cited By 10', NULL, NULL, '107-116', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Helping users avoid bugs in GUI applications', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-33244477851&partnerID=40&md5=4f91a116b40003ab05d09728883da438', NULL, '2005', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Michail2005', '1');
