INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000453', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Morariu2013', 'Cloud computing represents at this point the standard delivery method for the infrastructure and platform of next generation applications. The emergence of a wide range of commercial cloud services have changed not only the way code is written and maintained, but also the way it is executed. Private clouds play an important role in this new service delivery model being designed to provide computing capacity within the organization premises either standalone or in a hybrid model. As resources of the private cloud are limited, QoS assurance becomes an important challenge. This paper presents the design of a monitoring solution that integrates several open source tools and can assure QoS for private clouds. The solution is implemented for IBM CloudBurst 2.1 and IBM TSAM product stack and can monitor a wide range of services, from CPU and memory load to J2EE services and HTTP statistics generate real time alerts and provide integration with a Jira based issue tracking tools. The overall solution provides a closed loop QoS system for private clouds that is able to prevent a large set of issues and provide real time diagnostic data for root cause analysis.', NULL, NULL, 'O. Morariu and T. Borangiu and C. Morariu', '2013 24th International Workshop on Database and Expert Systems Applications', NULL, NULL, NULL, '10.1109/DEXA.2013.31', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'cloud computing;monitoring;program diagnostics;public domain software;quality of service;real-time systems;resource allocation;CPU;HTTP statistics;IBM CloudBurst 2.1;IBM TSAM product stack;J2EE services;Jira based issue tracking tools;QoS assurance;closed loop QoS system;cloud computing capacity;commercial cloud services;hybrid model;memory load;multilayer QoS monitoring;open source tools;private cloud resources;real time alerts;real time diagnostic data;root cause analysis;service delivery model;standard delivery method;Cloud computing;Instruction sets;Measurement;Monitoring;Quality of service;Real-time systems;IBM CloudBurst;diagnostic;private cloud;quality of service;real time monitoring;service monitoring', NULL, NULL, 'Aug', NULL, NULL, NULL, '236-240', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Multi-layer QoS Monitoring in Private Clouds', NULL, NULL, NULL, '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Morariu2013', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000455', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Murgia2014', 'Software development is a collaborative activity in which developers interact to create and maintain a complex software system. Human collaboration inevitably evokes emotions like joy or sadness, which can affect the collaboration either positively or negatively, yet not much is known about the individual emotions and their role for software development stakeholders. In this study, we analyze whether development artifacts like issue reports carry any emotional information about software development. This is a first step towards verifying the feasibility of an automatic tool for emotion mining in software development artifacts: if humans cannot determine any emotion from a software artifact, neither can a tool. Analysis of the Apache Software Foundation issue tracking system shows that developers do express emotions (in particular gratitude, joy and sadness). However, the more context is provided about an issue report, the more human raters start to doubt and nuance their interpretation of emotions. More investigation is needed before building a fully automatic emotion mining tool. Copyright is held by the author/owner(s). Publication rights licensed to ACM.', NULL, NULL, 'Murgia, A.a and Tourani, P.b and Adams, B.b and Ortu, M.c ', NULL, NULL, NULL, NULL, '10.1145/2597073.2597086', NULL, NULL, NULL, NULL, NULL, NULL, '11th Working Conference on Mining Software Repositories, MSR 2014 - Proceedings', NULL, NULL, NULL, NULL, NULL, 'cited By 9', NULL, NULL, '262-271', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Do developers feel emotions? An exploratory analysis of emotions in software artifacts', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84938816347&partnerID=40&md5=4026a33b8ade87144c7273a64c4d4cfa', NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Murgia2014', '1');
