INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000475', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Rastogi:2013:SMI:2442754.2442757', NULL, 'New York, NY, USA', NULL, 'Rastogi, Ayushi and Gupta, Arpit and Sureka, Ashish', 'Proceedings of the 6th India Software Engineering Conference', NULL, NULL, NULL, '10.1145/2442754.2442757', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'developer contribution assessment, issue tracking system, mining software repositories, software maintenance', NULL, 'New Delhi, India', NULL, NULL, NULL, NULL, '13--22', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'ISEC ''13', 'Samiksha: Mining Issue Tracking System for Contribution and Performance Assessment', NULL, 'http://doi.acm.org/10.1145/2442754.2442757', NULL, '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Rastogi:2013:SMI:2442754.2442757', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000476', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Rastogi2014', 'Free/Libre Open Source Software (FLOSS) community management is an important issue. Contributor churn (joining or leaving a project) causes failure of the majority of software projects. In this paper, we present a framework to characterize stability of the community in software maintenance projects by mining Issue Tracking System (ITS). We identify key stability indicators and propose metrics to measure them. We conduct time series analysis on metrics data to examine the stability of the community. We model community participation patterns and forecast future behavior to help plan and support informed decision making. We present a case study of four years data of Google Chromium Project and investigate the inferential ability of the framework.', NULL, NULL, 'A. Rastogi and A. Sureka', '2014 21st Asia-Pacific Software Engineering Conference', NULL, NULL, NULL, '10.1109/APSEC.2014.88', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'project management;public domain software;software management;system recovery;FLOSS community management;Google chromium project;ITS;community contribution pattern;decision making;free-libre open source software;inferential ability;issue tracking system;key stability indicators;software maintenance projects;software project;time series analysis;Analytical models;Computational modeling;Data models;Market research;Measurement;Predictive models;Stability analysis;Developer Contribution Patterns;Issue Tracking System;Mining Software Repositories', NULL, NULL, 'Dec', NULL, NULL, NULL, '31-34', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'What Community Contribution Pattern Says about Stability of Software Project?', NULL, NULL, '2', '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Rastogi2014', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000477', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Rastogi2013', 'Contribution and performance assessment is an established practice in organization with its sphere of influence spanning process, policy, people (client) and personnel (employee). Multiple roles in organization (for e.g. employees, project manager, hr manager etc.) view contribution and performance assessment with different perspectives and objectives. However, despite its ability to ensure growth and affluence of organization existing contribution and performance assessment practices are marred with flaws and imperfections. The objective of this work is to serve wide-ranged requirements of software maintenance professionals (in context of contribution and performance assessment) in organization by application of variegated approaches (tools, techniques, models etc.) on software repositories (Issue Tracking System, Version Control System, Source Code Repositories etc.). We study pain-points of practitioners (in context of contribution and performance assessment) and present solution approach by mining software repositories. We implement the approach on real-world data and gather insights from practitioners to validate and improve our research. In this paper, ''SamikshaUmbra'' (research umbrella that circumscribe related research threads) we present approach to meet stated research objectives and viable research directions.', NULL, NULL, 'A. Rastogi and A. Sureka', '2013 20th Asia-Pacific Software Engineering Conference (APSEC)', NULL, NULL, NULL, '10.1109/APSEC.2013.134', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'data mining;organisational aspects;research and development;software development management;software maintenance;SamikshaUmbra;contribution practice;issue tracking system;organization affluence;organization growth;performance assessment practice;related research threads;research directions;research objectives;research umbrella;software maintenance professionals;software repositories mining;source code repositories;version control system;Context;Data mining;Measurement;Organizations;Software maintenance;Standards organizations;Contribution and Performance Assessment;Mining Software Repositories;Software Maintenance Professionals', NULL, NULL, 'Dec', NULL, NULL, NULL, '170-175', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'SamikshaUmbra: Contribution and Performance Assessment of Software Maintenance Professionals by Mining Software Repositories', NULL, NULL, '2', '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Rastogi2013', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000478', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Romano2011', 'Uncaught exceptions, and in particular null pointer exceptions (NPEs), constitute a major cause of crashes for software systems. Although tools for the static identification of potential NPEs exist, there is need for proper approaches able to identify system execution scenarios causing NPEs. This paper proposes a search-based test data generation approach aimed at automatically identify NPEs. The approach consists of two steps: (i) an inter-procedural data and control flow analysis - relying on existing technology - that identifies paths between input parameters and potential NPEs, and (ii) a genetic algorithm that evolves a population of test data with the aim of covering such paths. The algorithm is able to deal with complex inputs containing arbitrary data structures. The approach has been evaluated on to test class clusters from six Java open source systems, where NPE bugs have been artificially introduced. Results show that the approach is, indeed, able to identify the NPE bugs, and it outperforms random testing. Also, we show how the approach is able to identify real NPE bugs some of which are posted in the bug-tracking system of the Apache libraries.', NULL, NULL, 'D. Romano and M. Di Penta and G. Antoniol', '2011 Fourth IEEE International Conference on Software Testing, Verification and Validation', NULL, NULL, NULL, '10.1109/ICST.2011.49', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Java;data structures;genetic algorithms;program control structures;program debugging;program testing;public domain software;software libraries;system recovery;Apache library;Java open source system;NPE bug;bug-tracking system;control flow analysis;data structure;genetic algorithm;interprocedural data;null pointer exception;search based testing;search-based test data generation;software system crash;system execution;test class cluster;Gallium;Genetic algorithms;Instruments;Java;Libraries;Null value;Testing;Null pointer exceptions;Search-based testing', NULL, NULL, 'March', NULL, NULL, NULL, '160-169', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'An Approach for Search Based Testing of Null Pointer Exceptions', NULL, NULL, NULL, '2011', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Romano2011', '1');
