INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000480', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Romo:2014:FGD:2641580.2641592', NULL, 'New York, NY, USA', NULL, 'Romo, Bilyaminu Auwal and Capiluppi, Andrea and Hall, Tracy', 'Proceedings of The International Symposium on Open Collaboration', NULL, NULL, NULL, '10.1145/2641580.2641592', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Bug traceability, bug-fixing commits', NULL, 'Berlin, Germany', NULL, NULL, NULL, NULL, '8:1--8:4', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'OpenSym ''14', 'Filling the Gaps of Development Logs and Bug Issue Data', NULL, 'http://doi.acm.org/10.1145/2641580.2641592', NULL, '2014', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Romo:2014:FGD:2641580.2641592', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000481', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Saha2015', 'Understanding the severity of reported bugs is important in both research and practice. In particular, a number of recently proposed mining-based software engineering techniques predict bug severity, bug report quality, and bug-fix time, according to this information. Many bug tracking systems provide a field ''severity'' offering options such as ''severe'', ''normal'', and ''minor'', with ''normal'' as the default. However, there is a widespread perception that for many bug reports the label ''normal'' may not reflect the actual severity, because reporters may overlook setting the severity or may not feel confident enough to do so. In many cases, researchers ignore ''normal'' bug reports, and thus overlook a large percentage of the reports provided. On the other hand, treating them all together risks mixing reports that have very diverse properties. In this study, we investigate the extent to which ''normal'' bug reports actually have the ''normal'' severity. We find that many ''normal'' bug reports in practice are not normal. Furthermore, this misclassification can have a significant impact on the accuracy of mining-based tools and studies that rely on bug report severity information. © 2015 IEEE.', NULL, NULL, 'Saha, R.K.a and Lawall, J.b and Khurshid, S.a and Perry, D.E.a ', NULL, NULL, NULL, NULL, '10.1109/MSR.2015.31', NULL, NULL, NULL, NULL, NULL, NULL, 'IEEE International Working Conference on Mining Software Repositories', NULL, NULL, NULL, NULL, NULL, 'cited By 0', NULL, NULL, '258-268', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Are these bugs really ''normal''?', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84957107517&partnerID=40&md5=1d0fefdfc0cd2a2fdc3d0a816212bbf6', '2015-August', '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Saha2015', '1');
