INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000485', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Sassc2013', 'The evolution of non-trivial software systems is accompanied by unexpected behaviour and side-effects, referred as bugs or defects. These defects are reported to and stored in bug tracking systems, which contain descriptions of the problems that have been encountered. However, bug tracking systems store and present bug reports in textual form, which makes their understanding dispersive and unintuitive. We present an approach to display bug reports through a web-based visual analytics platform, named in*Bug. in*Bug allows users to navigate and inspect the vast information space created by bug tracking systems, with the goal of easing the comprehension of bug reports in detail and also obtain an understanding “in the large” of how bugs are reported with respect to one system or to an entire software ecosystem.', NULL, NULL, 'T. Dal Sassc and M. Lanza', 'Software Visualization (VISSOFT), 2013 First IEEE Working Conference on', NULL, NULL, NULL, '10.1109/VISSOFT.2013.6650542', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program debugging;software maintenance;tracking;Web-based visual analytics platform;bug reports;bug tracking systems;in*Bug;nontrivial software system evolution;software ecosystem;Computer bugs;Data visualization;Ecosystems;Joining processes;Software;Visual analytics', NULL, NULL, 'Sept', NULL, NULL, NULL, '1-4', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'A closer look at bugs', NULL, NULL, NULL, '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Sassc2013', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000486', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Sasso:2014:MSD:2705615.2706091', NULL, 'Washington, DC, USA', NULL, 'Sasso, Tommaso Dal', 'Proceedings of the 2014 IEEE International Conference on Software Maintenance and Evolution', NULL, NULL, NULL, '10.1109/ICSME.2014.124', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '669--', NULL, NULL, NULL, NULL, 'IEEE Computer Society', NULL, NULL, NULL, NULL, NULL, 'ICSME ''14', 'Managing Software Defects', NULL, 'http://dx.doi.org/10.1109/ICSME.2014.124', NULL, '2014', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Sasso:2014:MSD:2705615.2706091', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000487', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Sasso2014', 'Bug tracking systems are used to track and store the defects reported during the life of software projects. The underlying repositories represent a valuable source of information used for example for defect prediction and program comprehension. However, bug tracking systems present the actual bugs essentially in textual form, which is not only cumbersome to navigate, but also hinders the understanding of the intricate pieces of information that revolve around software bugs. We present in*Bug, a web-based visual analytics platform to navigate and inspect bug repositories. in*Bug provides several interactive views to understand detailed information about the bugs and the people that report them. The tool can be downloaded at http://inbug.inf.usi.ch', NULL, NULL, 'T. Dal Sasso and M. Lanza', 'Software Maintenance, Reengineering and Reverse Engineering (CSMR-WCRE), 2014 Software Evolution Week - IEEE Conference on', NULL, NULL, NULL, '10.1109/CSMR-WCRE.2014.6747208', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'data visualisation;information storage;program debugging;software tools;Web-based visual analytics platform;bug repositories;bug tracking systems;defect prediction;in*Bug;information repositories;program comprehension;software bugs;software projects;Complexity theory;Computer bugs;Data visualization;Navigation;Software;Visual analytics', NULL, NULL, 'Feb', NULL, NULL, NULL, '415-419', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'In * bug: Visual analytics of bug repositories', NULL, NULL, NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Sasso2014', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000488', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Serrano2005', 'Bug-tracking helps the software developers in knowing what the error is, resolving it, and learning from it. Working on a software project includes managing the bugs we find. At first, we might list them on a spreadsheet. But when the number of bugs becomes too large and a lot of people must access and input data on them, we have to give up the spreadsheet and instead use a bug- or issue-tracking system. Many software projects reach this point, especially during testing and deployment when users tend to find an application''s bugs. Nowadays we can choose among dozens of bug-tracking systems. This paper looks at two specific open source products and provides useful hints for working with any bug-tracking tool.', NULL, NULL, 'N. Serrano and I. Ciordia', NULL, NULL, NULL, NULL, '10.1109/MS.2005.32', NULL, NULL, NULL, NULL, NULL, NULL, 'IEEE Software', NULL, 'program debugging;program diagnostics;program testing;public domain software;software tools;bug-tracking system;bug-tracking tool;open source product;program debugging;program diagnostics;program testing;Application software;Computer bugs;Control systems;Filters;Information systems;Linux;NASA;Open source software;Software tools;Spatial databases;bug database;bug tracker;issues database', NULL, NULL, 'March', NULL, '2', NULL, '11-13', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Bugzilla, ITracker, and other bug trackers', NULL, NULL, '22', '2005', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Serrano2005', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000490', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Shokripour2012', 'The number of reported bugs in large open source projects is high and triaging these bugs is an important issue in software maintenance. As a step in the bug triaging process, assigning a new bug to the most appropriate developer to fix it, is not only a time-consuming and tedious task. The triager, the person who considers a bug and assigns it to a developer, also needs to be aware of developer activities at different parts of the project. It is clear that only a few developers have this ability to carry out this step of bug triaging. The main goal of this paper is to suggest a new approach to the process of performing automatic bug assignment. The information needed to select the best developers to fix a new bug report is extracted from the version control repository of the project. Unlike all the previous suggested approaches which used Machine Learning and Information Retrieval methods, this research employs the Information Extraction (IE) methods to extract the information from the software repositories. The proposed approach does not use the information of the bug repository to make decisions about bugs in order to obtain better results on projects which do not have many fixed bugs. The aim of this research is to recommend the actual fixers of the bugs. Using this approach, we achieved 62%, 43% and 41% accuracies on Eclipse, Mozilla and Gnome projects, respectively.', NULL, NULL, 'R. Shokripour and Z. M. Kasirun and S. Zamani and J. Anvik', 'Advanced Computer Science Applications and Technologies (ACSAT), 2012 International Conference on', NULL, NULL, NULL, '10.1109/ACSAT.2012.56', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'information retrieval;program debugging;public domain software;software maintenance;Eclipse projects;Gnome projects;IE methods;Mozilla projects;automatic bug assignment;bug repository;bug triaging process;developer activity;fixed bugs;information extraction methods;information retrieval methods;machine learning;open source projects;reported bugs;software maintenance;software repository;tedious task;time-consuming task;version control repository;Bug Assignment;File Activity Histories;Information Extraction;Named Entity Recognition', NULL, NULL, 'Nov', NULL, NULL, NULL, '144-149', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Automatic Bug Assignment Using Information Extraction Methods', NULL, NULL, NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Shokripour2012', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000491', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Singh2011', 'Tracking of a reported bug for fixing is a fascinating area of research in software engineering. Many open source, free and commercial bug tracking tools have been developed and are currently under development. The industry needs criteria to select the best tool among the available set of tools that will help in fixing and tracking the progress of bug fixes. In this paper, we use BugZilla, Jira, Trac, Mantis, BugTracker.Net, Gnats and Fossil for comparative study. We present a comprehensive classification criteria to review the available tools and propose a new tool named Bug Tracking and Reliability Assessment System (BTRAS) for the bug tracking/reporting and reliability assessment. BTRAS helps in reporting the bug, assigning the bug to the developer for fixing, monitoring the progress of bug fixing by various graphical/charting facility and status updates, providing reliability bug prediction and bug complexity measurements, and distributing fixes to users/developers.', NULL, NULL, 'Singh, V.B.a and Chaturvedi, K.K.b ', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'International Journal of Software Engineering and its Applications', NULL, NULL, NULL, NULL, NULL, 'cited By 3', '4', NULL, '17-30', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Bug tracking and reliability assessment system (BTRAS)', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84859810820&partnerID=40&md5=9dad3d0476ada67a76c7a7782b694c1a', '5', '2011', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Singh2011', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000492', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Singh2010', 'This, paper presents (i) several software reliability, growth models (SRGM) which tries to predict, quantitatively the failure, phenomena, in, an, Open, Source, Software, project over a period of time. Here, it is assumed that the number of failures during testing is dependent upon the number of instructions execution, (ii) in order to cater the, irregular state, of, bug-report, phenomena, on, the, bug tracking system and irregular fluctuation in terms of noise, the reliability models have been proposed by applying an Itô type Stochastic Differential Equations (SDE). We have demonstrated that proposed models can support management in building reliable software systems by predicting remaining bugs. We have also compared our proposed models with several existing models on the basis of different comparison criteria.', NULL, NULL, 'V. B. Singh and P. K. Kapur and A. Tandon', 'Software Engineering (WCSE), 2010 Second World Congress on', NULL, NULL, NULL, '10.1109/WCSE.2010.149', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'differential equations;public domain software;software reliability;bug report phenomena;open source software;software reliability growth model;software system;stochastic differential equation;support management;Data models;Mathematical model;Software;Software reliability;Stochastic processes;Testing', NULL, NULL, 'Dec', NULL, NULL, NULL, '115-118', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Measuring Reliability Growth of Open Source Software by Applying Stochastic Differential Equations', NULL, NULL, '2', '2010', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Singh2010', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000493', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Somasundaram:2012:ACB:2134254.2134276', NULL, 'New York, NY, USA', NULL, 'Somasundaram, Kalyanasundaram and Murphy, Gail C.', 'Proceedings of the 5th India Software Engineering Conference', NULL, NULL, NULL, '10.1145/2134254.2134276', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'component recommendation, recommendation system, software bug triage', NULL, 'Kanpur, India', NULL, NULL, NULL, NULL, '125--130', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'ISEC ''12', 'Automatic Categorization of Bug Reports Using Latent Dirichlet Allocation', NULL, 'http://doi.acm.org/10.1145/2134254.2134276', NULL, '2012', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Somasundaram:2012:ACB:2134254.2134276', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000497', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Song2010a', 'Both developers and users submit bug reports to a bug repository. These reports can help reveal defects and improve software quality. As the number of bug reports in a bug repository increases, the number of the potential duplicate bug reports increases. Detecting duplicate bug reports helps reduce development efforts in fixing defects. However, it is challenging to manually detect all potential duplicates because of the large number of existing bug reports. This paper presents JDF (representing Jazz Duplicate Finder), a tool that helps users to find potential duplicates of bug reports on Jazz, which is a team collaboration platform for software development and process management. JDF finds potential duplicates for a given bug report using natural language and execution information. © 2010 ACM.', NULL, NULL, 'Song, Y.a and Wang, X.b c and Xie, T.a and Zhang, L.b c and Mei, H.b c ', NULL, NULL, NULL, NULL, '10.1145/1810295.1810368', NULL, NULL, NULL, NULL, NULL, NULL, 'Proceedings - International Conference on Software Engineering', NULL, NULL, NULL, NULL, NULL, 'cited By 3', NULL, NULL, '315-316', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'JDF: Detecting duplicate bug reports in Jazz', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-77954739345&partnerID=40&md5=2f9daab38496dfe301765ef9af67519d', '2', '2010', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Song2010a', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000498', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Squire2014', 'Software forges are centralized online systems that provide useful tools to help distributed development teams work together, especially in free, libre, and open source software (FLOSS). Forge-provided tools may include web space, version control systems, mailing lists and communication forums, bug tracking systems, file downloads, wikis, and the like. Empirical software engineering researchers can mine the artifacts from these tools to better understand how FLOSS is made. As the landscape of distributed software development has grown and changed, the tools needed to make FLOSS have changed as well. There are three newer tools at the center of FLOSS development today: distributed version control based forges (like Github), programmer question-and-answer communities (like Stack Overflow), and paste bin tools (like Gist or Pastebin.com). These tools are extending and changing the toolset used for FLOSS development, and redefining what a software forge looks like. The main contributions of this paper are to describe each of these tools, to identify the data and artifacts available for mining from these tools, and to outline some of the ways researchers can use these artifacts to continue to understand how FLOSS is made.', NULL, NULL, 'M. Squire', '2014 47th Hawaii International Conference on System Sciences', NULL, NULL, NULL, '10.1109/HICSS.2014.405', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Internet;data mining;distributed processing;public domain software;software engineering;software tools;team working;FLOSS development;Forge++;Gist;Github;Pastebin.com;Stack Overflow;centralized online systems;data mining;distributed development teams;distributed software development;distributed version control based forges;free-libre-open source software;pastebin tools;programmer question-and-answer communities;software forges;Communities;Computer languages;Control systems;Data mining;Google;Licenses;Software;forges;github;open source software;pastebin;repositories;software development;stack overflow', NULL, NULL, 'Jan', NULL, NULL, NULL, '3266-3275', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Forge++: The Changing Landscape of FLOSS Development', NULL, NULL, NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Squire2014', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000499', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Stepalin2010', 'In recent days more and more software developments tools become distributed by the SaaS (Software-As-A Service) model alongside with ready-to-install products. The developers of task and bug tracking systems now offer their solutions by a monthly fee. For instance, JIRA Studio produced by Atlassian can be connected to a corporative domain by subscription. This scheme allows software companies to reduce costs at the project''s start and get scalable resources in future. Software documentation systems can also be purchased by a subscription now. The effectiveness of their usage for various documentation development is interesting. There are four major types of documentation supporting the development process and resulted products: project, technical, code and user documentation. Each of this type claims specific requirements for the documentation tool. The requirement analysis shows that rented documentation systems are the most appropriate for user and technical documentation. There are two major classes of software documentation systems: 1) Wiki, 2) DITA-orientedXML CMS. The following wiki systems have a hosted version: commercial Confluence, Central Desktop, EditMe, Incentive, Netcipia, PBWiki, Wikia, Wikispaces; open source BusinessWiki, Metadot Wiki, MindTouch, Wagn, Wikidot. The richest by the functionality andplugin collection is Confluence produced by Atlassian. The following XML CMS are offered by a SaaS model (all are commercial): Astoria On Demand, DITA Exchange. DocZone. SaaS is optionally supported in Bluestream XDocs, Siberlogic SiberSafe, Trisoft Infoshare, Vasont, X-Hive Docato. As wiki system is a ready integrated environment for creating and publishing documentation, DITA-system consists not only of XML CMS. To deploy a DITA-system, you should have an XML editor, publisher and CMS. The listed CMS can be integrated with top DITA XML editors and provide an API to integrate with other editors. These CMS also have build-in tools to export documents in mu- - ltiple formats. However, the universal component architecture of DITA-systems makes the deployment and configuration more difficult than wiki implementation. Hosted documentation systems are offered by different prices. The offerings of top documentation systems are considered in this paper. Wiki subscription fees range from 4,95$ (EditMe) to 20$ (Confluence) per one user/month. XML CMS subscription price starts from 500$ per month and can reach 12000$ per month. These subscriptions have no fixed price; in each individual case the CMS vendor performs a specific project of a DITA-system implementation. Wiki rental costs approximate to CMS subscriptions'' costs for large number of users, 500 and more. The advantages of renting a powerful documentation system for small and large project are the following: 1) Maximal functionality at a low affordable cost, 2) Platform independency and high system accessibility, 3) Document quality improvement at the expense of quality controlling tools application, 4) Higher effectiveness of documentation (content re-use, single source usage, automated tools for localization), 5) Organization of robust and scalable documentation process. As the SaaS business model becomes more popular, small companies get access to powerful software documentation systems, which are too expensive to purchase a standalone license at the startup. However, the system''s access security, reliability and information confidentiality issues remain opened and controversial.', NULL, NULL, 'E. Stepalina', 'Software Engineering Conference (CEE-SECR), 2010 6th Central and Eastern European', NULL, NULL, NULL, '10.1109/CEE-SECR.2010.5783175', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Web sites;XML;cloud computing;document handling;software engineering;systems analysis;τasont;Astoria On Demand;Atlassian;Bluestream XDocs;Central Desktop;Confluence;DITA Exchange;DITA-orientedXML CMS;DocZone;EditMe;Incentive;JIRA Studio;Metadot Wiki;MindTouch;Netcipia;PBWiki;SaaS business model;SaaS support;Siberlogic SiberSafe;Trisoft Infoshare;Wagn;Wikia;Wikidot;Wikispaces;X-Hive Docato;bug tracking systems;documentation publishing;open source BusinessWiki;quality controlling tools application;ready-to-install products;requirement analysis;software companies;software developments tools;software documentation systems;task tracking systems;Documentation;Electronic publishing;Google;Information services;Internet;Subscriptions;XML;SaaS;Software documentation;XML CMS;wiki', NULL, NULL, 'Oct', NULL, NULL, NULL, '192-197', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'SaaS support in software documentation systems', NULL, NULL, NULL, '2010', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Stepalin2010', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000500', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Sun2011', 'In a bug tracking system, different testers or users may submit multiple reports on the same bugs, referred to as duplicates, which may cost extra maintenance efforts in triaging and fixing bugs. In order to identify such duplicates accurately, in this paper we propose a retrieval function (REP) to measure the similarity between two bug reports. It fully utilizes the information available in a bug report including not only the similarity of textual content in summary and description fields, but also similarity of non-textual fields such as product, component, version, etc. For more accurate measurement of textual similarity, we extend BM25F - an effective similarity formula in information retrieval community, specially for duplicate report retrieval. Lastly we use a two-round stochastic gradient descent to automatically optimize REP for specific bug repositories in a supervised learning manner. We have validated our technique on three large software bug repositories from Mozilla, Eclipse and OpenOffice. The experiments show 10-27% relative improvement in recall rate@k and 17-23% relative improvement in mean average precision over our previous model. We also applied our technique to a very large dataset consisting of 209,058 reports from Eclipse, resulting in a recall rate@k of 37-71% and mean average precision of 47%.', NULL, NULL, 'C. Sun and D. Lo and S. C. Khoo and J. Jiang', 'Automated Software Engineering (ASE), 2011 26th IEEE/ACM International Conference on', NULL, NULL, NULL, '10.1109/ASE.2011.6100061', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'gradient methods;information retrieval;learning (artificial intelligence);program debugging;BM25F;REP;bug tracking system;duplicate bug reports;duplicate report retrieval;information retrieval community;retrieval function;software bug repositories;supervised learning;textual similarity;two-round stochastic gradient descent;Accuracy;Computer bugs;Information retrieval;Software;Support vector machines;Training;Tuning', NULL, NULL, 'Nov', NULL, NULL, NULL, '253-262', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Towards more accurate retrieval of duplicate bug reports', NULL, NULL, NULL, '2011', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Sun2011', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000502', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Sun:2010:DMA:1806799.1806811', NULL, 'New York, NY, USA', NULL, 'Sun, Chengnian and Lo, David and Wang, Xiaoyin and Jiang, Jing and Khoo, Siau-Cheng', 'Proceedings of the 32Nd ACM/IEEE International Conference on Software Engineering - Volume 1', NULL, NULL, NULL, '10.1145/1806799.1806811', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Cape Town, South Africa', NULL, NULL, NULL, NULL, '45--54', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'ICSE ''10', 'A Discriminative Model Approach for Accurate Duplicate Bug Report Retrieval', NULL, 'http://doi.acm.org/10.1145/1806799.1806811', NULL, '2010', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Sun:2010:DMA:1806799.1806811', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000503', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Takama2013', 'This paper proposes to apply information visualization technologies to the support of monitoring bug update information sent from multiple bug tracking systems. Bug update information managed by bug tracking systems (BTS) is one of text stream data, which continuously generates new data. Therefore, it is difficult for users to watch it all the time. In other words, the task of monitoring stream data inevitably involves breaks of the task, which would lose the context of monitoring. However, to the best of our knowledge, interaction design when involving breaks has not been fully studied yet. The proposed system visualizes the dynamic relationship between bugs with animation, and helps a user grasping the context of monitoring by highlighting updated bugs and the replay of animation for part of the last monitoring time. The effectiveness of the system is evaluated through experiments with test participants. Recent growth of the Web has brought us various kinds of text stream data, such as bulletin board systems (BBS), blogs, and social networking services (SNS). As such data is expected to be important resources for human support robots, this paper would contribute to interaction design of such robots.', NULL, NULL, 'Y. Takama and T. Kurosawa', 'Industrial Electronics (ISIE), 2013 IEEE International Symposium on', NULL, NULL, NULL, '10.1109/ISIE.2013.6563851', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Animation;Computer bugs;Context;Data visualization;Monitoring;Software;Visualization;bug tracking system;information visualization;monitoring support;stream data visualization', NULL, NULL, 'May', NULL, NULL, NULL, '1-5', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Application of monitoring support visualization to bug tracking systems', NULL, NULL, NULL, '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Takama2013', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000504', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Tamura2008', 'Open source software which serve as key components of critical infrastructures in the social life are still ever-expanding now. We focus on the quality problems of open source software developed under open source project. In case of considering the effect of the debugging process in the development of a method of reliability assessment for open source project, it is necessary to grasp the deeply-intertwined factors, such as programming path, size of each component, skill of fault reporters, and so on. Considering from a point of view of the software reliability growth model, it is difficult to cover the software fault-report phenomena on the bug tracking system of open source software. We propose a new approach to software reliability assessment based on deterministic chaos theory. Also, we analyze actual software fault count data to show numerical examples of software reliability assessment for the open source software.', NULL, NULL, 'Y. Tamura and S. Yamada', 'Secure System Integration and Reliability Improvement, 2008. SSIRI ''08. Second International Conference on', NULL, NULL, NULL, '10.1109/SSIRI.2008.14', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'chaos;public domain software;software engineering;software reliability;bug tracking system;debugging process;deterministic chaos theory;open source software;software reliability assessment;Chaos;Debugging;Fault detection;Linux;Open source software;Programming;Quality management;Reliability engineering;Reliability theory;Software reliability;Deterministic chaos theory;Open source software;Reliability', NULL, NULL, 'July', NULL, NULL, NULL, '60-66', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'A Method of Reliability Assessment Based on Deterministic Chaos Theory for an Open Source Software', NULL, NULL, NULL, '2008', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Tamura2008', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000505', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Tamura2007', 'All over the world people can gain the information at the same time by growing rate of Internet access around the world in recent years. In accordance with such a penetration of the Internet, it is increasing public awareness of the importance of online real-time and interactive functions. Therefore, software development environment has been changing into new development paradigms such as concurrent distributed development environment and the so-called open source project by using network computing technologies. Especially, such OSS (open source software) systems which serve as key components of critical infrastructures in our society are still ever-expanding now. In this paper, we propose a software reliability growth model based on stochastic differential equations in order to consider the active state of the open source project. Especially, we assume that the software failure intensity depends on the time, and the software fault-report phenomena on the bug tracking system keep an irregular state. Also, we analyze actual software fault count data to show numerical examples of software reliability assessment for the OSS. Moreover, we compare our model with the conventional model based on stochastic differential equations in terms of goodness-of-fit for actual data. We show that the proposed model can assist improvement of quality for OSS systems developed under the open source project.', NULL, NULL, 'Y. Tamura and S. Yamada', '2007 IEEE International Conference on Mechatronics', NULL, NULL, NULL, '10.1109/ICMECH.2007.4279994', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'differential equations;public domain software;software fault tolerance;software reliability;stochastic processes;network computing technology;open source software;software development environment;software fault-report phenomena;software reliability growth model;stochastic differential equation;Computer network reliability;Computer networks;Computer science;Differential equations;Internet;Mechatronics;Open source software;Programming;Software reliability;Stochastic processes;Open source software;reliability assessment;software reliability growth model;stochastic differential equation', NULL, NULL, 'May', NULL, NULL, NULL, '1-6', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Software Reliability Growth Model Based on Stochastic Differential Equations for Open Source Software', NULL, NULL, NULL, '2007', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Tamura2007', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000506', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Tappolet:2010:SWE:1833903.1834015', NULL, 'Amsterdam, The Netherlands, The Netherlands', NULL, 'Tappolet, Jonas and Kiefer, Christoph and Bernstein, Abraham', NULL, NULL, NULL, NULL, '10.1016/j.websem.2010.04.009', NULL, NULL, NULL, NULL, NULL, NULL, 'Web Semant.', NULL, 'Bug prediction, Software comprehension framework, Software evolution, Software release similarity', NULL, NULL, '#jul#', NULL, '2-3', NULL, '225--240', NULL, NULL, NULL, NULL, 'Elsevier Science Publishers B. V.', NULL, NULL, NULL, NULL, NULL, NULL, 'Semantic Web Enabled Software Analysis', NULL, 'http://dx.doi.org/10.1016/j.websem.2010.04.009', '8', '2010', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Tappolet:2010:SWE:1833903.1834015', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000507', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Tappolet2010', 'One of the most important decisions researchers face when analyzing software systems is the choice of a proper data analysis/exchange format. In this paper, we present EvoOnt, a set of software ontologies and data exchange formats based on OWL. EvoOnt models software design, release history information, and bug-tracking meta-data. Since OWL describes the semantics of the data, EvoOnt (1) is easily extendible, (2) can be processed with many existing tools, and (3) allows to derive assertions through its inherent Description Logic reasoning capabilities. The contribution of this paper is that it introduces a novel software evolution ontology that vastly simplifies typical software evolution analysis tasks. In detail, we show the usefulness of EvoOnt by repeating selected software evolution and analysis experiments from the 2004-2007 Mining Software Repositories Workshops (MSR). We demonstrate that if the data used for analysis were available in EvoOnt then the analyses in 75% of the papers at MSR could be reduced to one or at most two simple queries within off-the-shelf SPARQL tools. In addition, we present how the inherent capabilities of the Semantic Web have the potential of enabling new tasks that have not yet been addressed by software evolution researchers, e.g., due to the complexities of the data integration. © 2010 Elsevier B.V.', NULL, NULL, 'Tappolet, J. and Kiefer, C. and Bernstein, A.', NULL, NULL, NULL, NULL, '10.1016/j.websem.2010.04.009', NULL, NULL, NULL, NULL, NULL, NULL, 'Journal of Web Semantics', NULL, NULL, NULL, NULL, NULL, 'cited By 12', '2-3', NULL, '225-240', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Semantic web enabled software analysis', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-77955230399&partnerID=40&md5=b93313ee75158ed39f6354a2a0a3efe2', '8', '2010', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Tappolet2010', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000508', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Thung2014', 'To track bugs that appear in a software, developers often make use of a bug tracking system. Users can report bugs that they encounter in such a system. Bug reporting is inherently an uncoordinated distributed process though and thus when a user submits a new bug report, there might be cases when another bug report describing exactly the same problem is already present in the system. Such bug reports are duplicate of each other and these duplicate bug reports need to be identified. A number of past studies have proposed a number of automated approaches to detect duplicate bug reports. However, these approaches are not integrated to existing bug tracking systems. In this paper, we propose a tool named DupFinder, which implements the state-of-theart unsupervised duplicate bug report approach by Runeson et al., as a Bugzilla extension. DupFinder does not require any training data and thus can easily be deployed to any project. DupFinder extracts texts from summary and description fields of a new bug report and recent bug reports present in a bug tracking system, uses vector space model to measure similarity of bug reports, and provides developers with a list of potential duplicate bug reports based on the similarity of these reports with the new bug report. We have released DupFinder as an open source tool in GitHub, which is available at: uhttps://github.com/smagsmu/dupfinder. © 2014 ACM.', NULL, NULL, 'Thung, F. and Kochhar, P.S. and Lo, D.', NULL, NULL, NULL, NULL, '10.1145/2642937.2648627', NULL, NULL, NULL, NULL, NULL, NULL, 'ASE 2014 - Proceedings of the 29th ACM/IEEE International Conference on Automated Software Engineering', NULL, NULL, NULL, NULL, NULL, 'cited By 0', NULL, NULL, '871-874', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'DupFinder: Integrated tool support for duplicate bug report detection', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84908618742&partnerID=40&md5=e5a6bc1465b1ae05c0f10dec9c6ae212', NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Thung2014', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000510', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Thung2014a', 'To track bugs that appear in a software, developers often make use of a bug tracking system. Users can report bugs that they encounter in such a system. Bug reporting is inherently an uncoordinated distributed process though and thus when a user submits a new bug report, there might be cases when another bug report describing exactly the same problem is already present in the system. Such bug reports are duplicate of each other and these duplicate bug reports need to be identified. A number of past studies have proposed a number of automated approaches to detect duplicate bug reports. However, these approaches are not integrated to existing bug tracking systems. In this paper, we propose a tool named DupFinder, which implements the state-of-theart unsupervised duplicate bug report approach by Runeson et al., as a Bugzilla extension. DupFinder does not require any training data and thus can easily be deployed to any project. DupFinder extracts texts from summary and description fields of a new bug report and recent bug reports present in a bug tracking system, uses vector space model to measure similarity of bug reports, and provides developers with a list of potential duplicate bug reports based on the similarity of these reports with the new bug report. We have released DupFinder as an open source tool in GitHub, which is available at: uhttps://github.com/smagsmu/dupfinder. &copy; 2014 ACM.', 'Vasteras, Sweden', NULL, 'Thung, Ferdian and Kochhar, Pavneet Singh and Lo, David', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'ASE 2014 - Proceedings of the 29th ACM/IEEE International Conference on Automated Software Engineering', 'Program debugging', 'Open source software;Software engineering;Tracking (position);Vector spaces;', 'English', NULL, NULL, 'Automated approach;Bug tracking system;Bugzilla;Distributed process;Duplicate bug reports;Integrated tools;Open source tools;Vector space models;', NULL, NULL, '871 - 874', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'DupFinder: Integrated tool support for duplicate bug report detection', NULL, 'http://dx.doi.org/10.1145/2642937.2648627', NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:6]', 'Thung2014a', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000511', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Thung:2014:BIT:2635868.2661678', NULL, 'New York, NY, USA', NULL, 'Thung, Ferdian and Le, Tien-Duy B. and Kochhar, Pavneet Singh and Lo, David', 'Proceedings of the 22Nd ACM SIGSOFT International Symposium on Foundations of Software Engineering', NULL, NULL, NULL, '10.1145/2635868.2661678', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Bug localization, Bugzilla, git', NULL, 'Hong Kong, China', NULL, NULL, NULL, NULL, '767--770', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'FSE 2014', 'BugLocalizer: Integrated Tool Support for Bug Localization', NULL, 'http://doi.acm.org/10.1145/2635868.2661678', NULL, '2014', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Thung:2014:BIT:2635868.2661678', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000512', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Thung2013', 'What is the root cause of this failure? This question is often among the first few asked by software debuggers when they try to address issues raised by a bug report. Root cause is the erroneous lines of code that cause a chain of erroneous program states eventually leading to the failure. Bug tracking and source control systems only record the symptoms (e.g., bug reports) and treatments of a bug (e.g., committed changes that fix the bug), but not its root cause. Many treatments contain non-essential changes, which are intermingled with root causes. Reverse engineering the root cause of a bug can help to understand why the bug is introduced and help to detect and prevent other bugs of similar causes. The recovered root causes are also better ground truth for bug detection and localization studies. In this work, we propose a combination of machine learning and code analysis techniques to identify root causes from the changes made to fix bugs. We evaluate the effectiveness of our approach based on a golden set (i.e., ground truth data) of manually recovered root causes of 200 bug reports from three open source projects. Our approach is able to achieve a precision, recall, and F-measure (i.e., the harmonic mean of precision and recall) of 76.42%, 71.88%, and 74.08% respectively. Compared with the work by Kawrykow and Robillard, our approach achieves a 60.83% improvement in F-measure.', NULL, NULL, 'F. Thung and D. Lo and L. Jiang', '2013 20th Working Conference on Reverse Engineering (WCRE)', NULL, NULL, NULL, '10.1109/WCRE.2013.6671284', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'learning (artificial intelligence);program debugging;program diagnostics;reverse engineering;F-measure;automatic root cause recovery;bug detection;bug localization;bug reports;bug-fixing changes;code analysis techniques;machine learning;open source projects;reverse engineering;Computer bugs;Context;Control systems;Data models;Feature extraction;Support vector machines;Training data', NULL, NULL, 'Oct', NULL, NULL, NULL, '92-101', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Automatic recovery of root causes from bug-fixing changes', NULL, NULL, NULL, '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Thung2013', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000513', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Thung2012', 'Not all bugs in software would be experienced and reported by end users right away: Some bugs manifest themselves quickly and may be reported by users a few days after they get into the code base; others manifest many months or even years later, and may only be experienced and reported by a small number of users. We refer to the period of time between the time when a bug is introduced into code and the time when it is reported by a user as bug reporting latency. Knowledge of bug reporting latencies has an implication on prioritization of bug fixing activities-bugs with low reporting latencies may be fixed earlier than those with high latencies to shift debugging resources towards bugs highly concerning users. To investigate bug reporting latencies, we analyze bugs from three Java software systems: AspectJ, Rhino, and Lucene. We extract bug reporting data from their version control repositories and bug tracking systems, identify bug locations based on bug fixes, and back-trace bug introducing time based on change histories of the buggy code. Also, we remove non-essential changes, and most importantly, recover root causes of bugs from their treatments/fixes. We then calculate the bug reporting latencies, and find that bugs have diverse reporting latencies. Based on the calculated reporting latencies and features we extract from bugs, we build classification models that can predict whether a bug would be reported early (within 30 days) or later, which may be helpful for prioritizing bug fixing activities. Our evaluation on the three software systems shows that our bug reporting latency prediction models could achieve an AUC (Area Under the Receiving Operating Characteristics Curve) of 70.869%.', NULL, NULL, 'F. Thung and D. Lo and L. Jiang and Lucia and F. Rahman and P. T. Devanbu', 'Software Maintenance (ICSM), 2012 28th IEEE International Conference on', NULL, NULL, NULL, '10.1109/ICSM.2012.6405302', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Java;aspect-oriented programming;pattern classification;program debugging;AUC;AspectJ;Java software systems;Lucene;Rhino;area under-the-receiving operating characteristics curve;back-trace bug;bug fixing activities;bug location identification;bug reporting data extraction;bug reporting latency prediction models;bug tracking systems;classification models;debugging resources;software bugs;software systems;version control repositories;Computer bugs;Conferences;Databases;Feature extraction;Java;Predictive models;Software systems', NULL, NULL, 'Sept', NULL, NULL, NULL, '420-429', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'When would this bug get reported?', NULL, NULL, NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Thung2012', '1');
