INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000517', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Tian2012', 'Bugs are prevalent in software systems. To improve the reliability of software systems, developers often allow end users to provide feedback on bugs that they encounter. Users could perform this by sending a bug report in a bug report management system like Bugzilla. This process however is uncoordinated and distributed, which means that many users could submit bug reports reporting the same problem. These are referred to as duplicate bug reports. The existence of many duplicate bug reports may cause much unnecessary manual efforts as often a triager would need to manually tag bug reports as being duplicates. Recently, there have been a number of studies that investigate duplicate bug report problem which in effect answer the following question: given a new bug report, retrieve k other similar bug reports. This, however, still requires substantive manual effort which could be reduced further. Jalbert and Weimer are the first to introduce the direct detection of duplicate bug reports; it answers the question: given a new bug report, classify if it as a duplicate bug report or not. In this paper, we extend Jalbert and Weimer''s work by improving the accuracy of automated duplicate bug report identification. We experiments with bug reports from Mozilla bug tracking system which were reported between February 2005 to October 2005, and find that we could improve the accuracy of the previous approach by about 160%. © 2012 IEEE.', NULL, NULL, 'Tian, Y.a and Sun, C.b and Lo, D.a ', NULL, NULL, NULL, NULL, '10.1109/CSMR.2012.48', NULL, NULL, NULL, NULL, NULL, NULL, 'Proceedings of the European Conference on Software Maintenance and Reengineering, CSMR', NULL, NULL, NULL, NULL, NULL, 'cited By 22', NULL, NULL, '385-390', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Improved duplicate bug report identification', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84860522295&partnerID=40&md5=4c519ee7a6e662e0364258aedcad5260', NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Tian2012', '1');
