INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000521', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Tong2015', 'Open source software is becoming increasingly important in cloud computing. However, many cloud computing systems suffer from software bugs that cause significant dependability issues. Bug assignment and fixing are crucial parts of software maintenance to improve dependability. In this paper, we conduct an empirical study of 42,880 bug reports from OpenStack bug repository. We study the characteristics (e.g., distribution of bugs, distribution of assignees) of bug assignments in OpenStack and find the bug assignment pattern which we call as long tail. The findings can support the follow-up research on improving efficiency of bug assignment, that is, we propose a prediction method based on long tail model, and experimentally evaluate this method by applying it to OpenStack bug assignment.', NULL, NULL, 'J. Tong and L. Ying and Y. Xiaoyong and T. Hongyan and W. Zhonghai', 'Trustworthy Systems and Their Applications (TSA), 2015 Second International Conference on', NULL, NULL, NULL, '10.1109/TSA.2015.14', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'cloud computing;program debugging;public domain software;software maintenance;OpenStack bug repository;bug assignment characteristics;bug assignment efficiency;bug assignment pattern;bug assignment prediction method;cloud computing systems;long tail model;open source software;software bugs;software dependability;software maintenance;Cloud computing;Computational modeling;Computer bugs;Correlation;Predictive models;bug assignment;cloud computing;long tail;open source', NULL, NULL, 'July', NULL, NULL, NULL, '16-23', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Characterizing and Predicting Bug Assignment in OpenStack', NULL, NULL, NULL, '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Tong2015', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000523', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Tran2015', 'Monitoring events on communication and computing systems becomes more and more challenging due to the increasing complexity and diversity of these systems. Several supporting tools have been created to assist system administrators in monitoring an enormous number of events daily. The main function of these tools is to filter as many as possible events and present non-trivial events to the administrators for fault analysis and detection. However, non-trivial events never decrease on large systems, such as cloud computing systems, while investigating events is time consuming. This paper proposes an approach for evaluating the severity level of an event using a classification and regression decision tree. The approach aims to build a decision tree based on the features of old events, then use this tree to decide the severity level of new events. The administrators take advantages of this decision to determine proper actions for the non-trivial events. We have implemented and experimented the approach for software bug datasets obtained from bug tracking systems. The experimental results reveal that the accuracy scores for different decision trees are above 70% and some detailed analyses are provided. © Springer International Publishing Switzerland 2015.', NULL, NULL, 'Tran, H.M. and Nguyen, S.V. and Le, S.T. and Vu, Q.T.', NULL, NULL, NULL, NULL, '10.1007/978-3-319-26135-5_5', NULL, NULL, NULL, NULL, NULL, NULL, 'Lecture Notes in Computer Science (including subseries Lecture Notes in Artificial Intelligence and Lecture Notes in Bioinformatics)', NULL, NULL, NULL, NULL, NULL, 'cited By 0', NULL, NULL, '57-71', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Fault data analytics using decision tree for fault detection', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84951958354&partnerID=40&md5=56a4854532a002cb873039536decb0a4', '9446', '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Tran2015', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000524', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Tu:2014:MQI:2677832.2677844', NULL, 'New York, NY, USA', NULL, 'Tu, Feifei and Zhang, Feixue', 'Proceedings of the 6th Asia-Pacific Symposium on Internetware on Internetware', NULL, NULL, NULL, '10.1145/2677832.2677844', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Data Quality, Issue Report, Quality Evaluator', NULL, 'Hong Kong, China', NULL, NULL, NULL, NULL, '76--79', NULL, NULL, NULL, NULL, 'ACM', NULL, NULL, NULL, NULL, NULL, 'INTERNETWARE 2014', 'Measuring the Quality of Issue Tracking Data', NULL, 'http://doi.acm.org/10.1145/2677832.2677844', NULL, '2014', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Tu:2014:MQI:2677832.2677844', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000525', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Vahabzadeh2015', 'Testing aims at detecting (regression) bugs in production code. However, testing code is just as likely to contain bugs as the code it tests. Buggy test cases can silently miss bugs in the production code or loudly ring false alarms when the production code is correct. We present the first empirical study of bugs in test code to characterize their prevalence and root cause categories. We mine the bug repositories and version control systems of 211 Apache Software Foundation (ASF) projects and find 5,556 test-related bug reports. We (1) compare properties of test bugs with production bugs, such as active time and fixing effort needed, and (2) qualitatively study 443 randomly sampled test bug reports in detail and categorize them based on their impact and root causes. Our results show that (1) around half of all the projects had bugs in their test code; (2) the majority of test bugs are false alarms, i.e., test fails while the production code is correct, while a minority of these bugs result in silent horrors, i.e., test passes while the production code is incorrect; (3) incorrect and missing assertions are the dominant root cause of silent horror bugs; (4) semantic (25%), flaky (21%), environment-related (18%) bugs are the dominant root cause categories of false alarms; (5) the majority of false alarm bugs happen in the exercise portion of the tests, and (6) developers contribute more actively to fixing test bugs and test bugs are fixed sooner compared to production bugs. In addition, we evaluate whether existing bug detection tools can detect bugs in test code. © 2015 IEEE.', NULL, NULL, 'Vahabzadeh, A. and Fard, A.M. and Mesbah, A.', NULL, NULL, NULL, NULL, '10.1109/ICSM.2015.7332456', NULL, NULL, NULL, NULL, NULL, NULL, '2015 IEEE 31st International Conference on Software Maintenance and Evolution, ICSME 2015 - Proceedings', NULL, NULL, NULL, NULL, NULL, 'cited By 0', NULL, NULL, '101-110', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'An empirical study of bugs in test code', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84961640892&partnerID=40&md5=dc9efaadb82d1e47ca337178cf645f6c', NULL, '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Vahabzadeh2015', '1');
