INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000528', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Varvaressos2014', 'Runtime verification is the process of observing a sequence of events generated by a running system and comparing it to some formal specification for potential violations. We show how the use of a runtime monitor can greatly speed up the testing phase of a video game under development, by automating the detection of bugs when the game is being played. We take advantage of the fact that a video game, contrarily to generic software, follows a special structure that contains a \"game loop\", this game loop can be used to centralize the instrumentation and generate events based on the game''s internal state. We report on experiments made on a sample of five real-world video games of various genres and sizes, by successfully incrementing and efficiently monitoring various temporal properties over their execution-including actual bugs reported in the games'' bug tracking database in the course of their development.', NULL, NULL, 'S. Varvaressos and K. Lavoie and A. B. Massé and S. Gaboury and S. Hallé', '2014 IEEE Seventh International Conference on Software Testing, Verification and Validation', NULL, NULL, NULL, '10.1109/ICST.2014.27', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'computer games;data flow analysis;formal specification;program debugging;program testing;program verification;automated bug finding;formal specification;runtime monitoring;runtime verification;video game testing phase;video games;Computer bugs;Games;Instruments;Monitoring;Runtime;Software;XML;runtime verification;temporal logic;video games', NULL, NULL, 'March', NULL, NULL, NULL, '143-152', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Automated Bug Finding in Video Games: A Case Study for Runtime Monitoring', NULL, NULL, NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Varvaressos2014', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000530', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Vijayakumar2014', 'Estimating the effort required to fix a bug is a significant task for the project manager to determine the project release. Among various ways to estimate the effort, analysis of bug report attributes proved excellent results. In this paper the effort required to fix the bug on the components of Firefox application is studied. A framework has been charted for analysing the feature attributes which on imparting association rule mining process resulted with dictating rules which provide the effort succumb to fix the bugs of a particular component. The bug reports used for this study are extracted from Bugzilla, an open source bug repository. These bug reports provides a variety of categorical data from previous projects. Analysis of this can improve the planning of personnel to fix the bug and raise the quality of bug reports.', NULL, NULL, 'K. Vijayakumar and V. Bhuvaneswari', 'Intelligent Computing Applications (ICICA), 2014 International Conference on', NULL, NULL, NULL, '10.1109/ICICA.2014.75', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'data mining;program debugging;public domain software;software development management;Bugzilla;Firefox application;association rule mining process;bug report attributes analysis;data mining approach;dictating rules;effort estimation;feature attributes;open source bug repository;personnel planning;project manager;project release;Association rules;Computer bugs;Estimation;Feature extraction;Software;Software testing;Bugzilla;association rules;bug report;component;data mining;repository;severity', NULL, NULL, 'March', NULL, NULL, NULL, '335-339', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'How Much Effort Needed to Fix the Bug? A Data Mining Approach for Effort Estimation and Analysing of Bug Report Attributes in Firefox', NULL, NULL, NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Vijayakumar2014', '1');
