INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000532', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Wang2011', 'Open source is an important model of collaborative knowledge work and virtual organizations. One of its work practices, peer review, is considered critical to its success, as Linus''s law highlights. Thus, understanding open source peer review, particular effective review practices, will improve the understanding of how to support collaborative work in new ways. Therefore, we conduct case studies in two open source communities that are well recognized as effective and successful, Mozilla and Python. In this paper, we present the preliminary results of our analysis on data from the bug tracking systems of those two organizations. We identify four common activities critical to open source software peer review, submission, identification, resolution and evaluation. Differences between communities indicate factors, such as reporter expertise, product type and structure, and organization size, affect review activities. We also discuss features of open source software peer review distinct from traditional review, as well as reconsiderations of Linus''s law.', NULL, NULL, 'J. Wang and J. M. Carroll', 'Collaboration Technologies and Systems (CTS), 2011 International Conference on', NULL, NULL, NULL, '10.1109/CTS.2011.5928673', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'groupware;program debugging;programming languages;public domain software;software performance evaluation;software reviews;virtual enterprises;Linus law;Mozilla;Python;bug tracking system;collaborative knowledge work;data analysis;evaluation;identification;open source community;open source software peer review practice;organization size;product structure;product type;reporter expertise;resolution;submission;virtual organization;Collaboration;Communities;Computer bugs;Fires;Open source software;Organizations;Coordination;cooperation and collaboration;designing collaborative & virtual organizations', NULL, NULL, 'May', NULL, NULL, NULL, '117-124', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Behind Linus''s law: A preliminary analysis of open source software peer review practices in Mozilla and Python', NULL, NULL, NULL, '2011', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Wang2011', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000534', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Wang2011a', 'A typical entry point for new developers in an open source project is to contribute a bug fix. However, finding an appropriate bug and an appropriate fix for that bug requires a good understanding of the project, which is nontrivial. Here, we extend Tesseract - an interactive project exploration environment - to allow new developers to search over bug descriptions in a project to quickly identify and explore bugs of interest and their related resources. More specifically, we extended Tesseract with search capabilities that enable synonyms and similar-bugs search over bug descriptions in a bug repository. The goal is to enable users to identify bugs of interest, resources related to that bug, (e.g., related files, contributing developers, communication records), and visually explore the appropriate socio-technical dependencies for the selected bug in an interactive manner. Here we present our search extension to Tesseract. © 2011 ACM.', NULL, NULL, 'Wang, J. and Sarma, A.', NULL, NULL, NULL, NULL, '10.1145/1984642.1984661', NULL, NULL, NULL, NULL, NULL, NULL, 'Proceedings - International Conference on Software Engineering', NULL, NULL, NULL, NULL, NULL, 'cited By 0', NULL, NULL, '76-79', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Which bug should I fix: Helping new developers onboard a new project', NULL, 'https://www.scopus.com/inward/record.uri?