INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000544', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Wijesiriwardana2012', 'Historical data about software projects is stored in repositories such as version control, bug tracking and mailing lists. Analyzing such data is vital to discover unthought-of-yet-interesting insights of a software project. Even though a wide range of software analysis techniques are already available, integration of such analyses is yet to be systematically addressed. Inspired from the recently introduced concept of Software as a Service, our research group investigated the concept of Software Analysis as a Service (SOFAS), a distributed and collaborative software analysis platform. SOFAS allows software analyses to be accessed, composed into workflows, and executed over the Internet. However, traditional service composition is a complex, time consuming and error-prone process, which requires experts in both composition languages and existing standards. In this paper, we propose a mashup platform to address the problem of software analysis composition in a light-weight, programming-free process-centric way. Our proposed mashup platform provides design-time guidance to the users throughout the mashup design by integrating a continuous feedback mechanism. It requires exploiting semantic web technologies and Software Engineering Ontologies (SEON).', NULL, NULL, 'C. Wijesiriwardana and G. Ghezzi and H. Gall', '2012 19th Asia-Pacific Software Engineering Conference', NULL, NULL, NULL, '10.1109/APSEC.2012.112', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'cloud computing;groupware;ontologies (artificial intelligence);program diagnostics;project management;semantic Web;software engineering;software standards;Internet;SEON;SOFAS;bug tracking;collaborative software analysis platform;composition languages;continuous feedback mechanism;design-time guidance;distributed software analysis;error-prone process;guided mashup framework;historical data;mailing lists;mashup platform;programming-free process-centric way;research group;semantic Web technology;software analysis as a service;software analysis composition;software analysis service composition;software analysis techniques;software as a service;software engineering ontology;software projects;software standards;version control;Data mining;History;Mashups;Measurement;Ontologies;Semantics;Mashups;Software Analysis as a Service;Software Engineering Ontologies', NULL, NULL, 'Dec', NULL, NULL, NULL, '725-728', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'A Guided Mashup Framework for Rapid Software Analysis Service Composition', NULL, NULL, '1', '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Wijesiriwardana2012', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000545', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Wong:2014:BBF:2705615.2706096', NULL, 'Washington, DC, USA', NULL, 'Wong, Chu-Pan and Xiong, Yingfei and Zhang, Hongyu and Hao, Dan and Zhang, Lu and Mei, Hong', 'Proceedings of the 2014 IEEE International Conference on Software Maintenance and Evolution', NULL, NULL, NULL, '10.1109/ICSME.2014.40', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'fault localization, bug report, feature location, information retrieval', NULL, NULL, NULL, NULL, NULL, NULL, '181--190', NULL, NULL, NULL, NULL, 'IEEE Computer Society', NULL, NULL, NULL, NULL, NULL, 'ICSME ''14', 'Boosting Bug-Report-Oriented Fault Localization with Segmentation and Stack-Trace Analysis', NULL, 'http://dx.doi.org/10.1109/ICSME.2014.40', NULL, '2014', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Wong:2014:BBF:2705615.2706096', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000546', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Wu2011a', 'Software bugs reported by human users and automatic error reporting software are often stored in some bug tracking tools (e.g., Bugzilia and Debbugs). These accumulated bug reports may contain valuable information that could be used to improve the quality of the bug reporting, reduce the quality assurance effort and cost, analyze software reliability, and predict future bug report trend. In this paper, we present BugMiner, a tool that is able to derive useful information from historic bug report database using data mining, use these information to do completion check and redundancy check on a new or given bug report, and to estimate the bug report trend using statistical analysis. Our empirical studies of the tool using several real-world bug report repositories show that it is effective, easy to implement, and has relatively high accuracy despite low quality data.', NULL, NULL, 'Wu, L. and Xie, B. and Kaiser, G. and Passonneau, R.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'SEKE 2011 - Proceedings of the 23rd International Conference on Software Engineering and Knowledge Engineering', NULL, NULL, NULL, NULL, NULL, 'cited By 4', NULL, NULL, '95-100', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'BugMiner: Software reliability analysis via data mining of bug reports', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84855528831&partnerID=40&md5=c6b995a94d2f115ed80794957b3c9536', NULL, '2011', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Wu2011a', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000547', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Wu2011b', 'Software bugs reported by human users and automatic error reporting software are often stored in some bug tracking tools (e.g., Bugzilia and Debbugs). These accumulated bug reports may contain valuable information that could be used to improve the quality of the bug reporting, reduce the quality assurance effort and cost, analyze software reliability, and predict future bug report trend. In this paper, we present BugMiner, a tool that is able to derive useful information from historic bug report database using data mining, use these information to do completion check and redundancy check on a new or given bug report, and to estimate the bug report trend using statistical analysis. Our empirical studies of the tool using several real-world bug report repositories show that it is effective, easy to implement, and has relatively high accuracy despite low quality data.', 'Miami, FL, United states', NULL, 'Wu, Leon and Xie, Boyi and Kaiser, Gail and Passonneau, Rebecca', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'SEKE 2011 - Proceedings of the 23rd International Conference on Software Engineering and Knowledge Engineering', 'Software reliability', 'Computer software selection and evaluation;Data mining;Information use;Knowledge engineering;Program debugging;Quality assurance;Reliability analysis;Software engineering;', 'English', NULL, NULL, 'Bug rep