INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000548', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Wu2011', 'This paper proposes a new approach called DREX (Developer Recommendation with k-nearest-neighbor search and Expertise ranking) to developer recommendation for bug resolution based on K-Nearest-Neighbor search with bug similarity and expertise ranking with various metrics, including simple frequency and social network metrics. We collect Mozilla Fire fox open bug repository as the experimental data set and compare different ranking metrics on the performance of recommending capable developers for bugs. Our experimental results demonstrate that, when recommending 10 developers for each one of the 250 testing bugs, DREX has produced better performance than traditional methods with multi-labeled text categorization. The best performance obtained by two metrics as Out-Degree and Frequency, is with recall as 0.6 on average. Moreover, other social network metrics such as Degree and Page Rank have produced comparable performance on developer recommendation as Frequency when used for developer expertise ranking.', NULL, NULL, 'W. Wu and W. Zhang and Y. Yang and Q. Wang', '2011 18th Asia-Pacific Software Engineering Conference', NULL, NULL, NULL, '10.1109/APSEC.2011.15', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program debugging;social networking (online);DREX;K-nearest-neighbor search;Mozilla Fire fox;Page Rank;bug repository;bug resolution;bug similarity;developer recommendation;expertise ranking;social network metrics;Computer bugs;Fires;Measurement;Social network services;Software;Testing;Vectors;Developer Recommendation;Expertise Ranking;KNN Search;Open Bug Repository', NULL, NULL, 'Dec', NULL, NULL, NULL, '389-396', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'DREX: Developer Recommendation with K-Nearest-Neighbor Search and Expertise Ranking', NULL, NULL, NULL, '2011', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Wu2011', '1');
