INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000550', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Xie2013', 'Triage is of great interest in software projects because it has the potential to reduce developer effort by involving a broader base of non-developer contributors to filter and augment reported issues. Using issue tracking data and interviews with experienced contributors we investigate ways to quantify the impact of triagers on reducing the number of issues developers need to resolve in two OSS projects: Mozilla and Gnome. We find the primary impact of triagers to involve issue filtering, filling missing information, and determining the relevant product. While triagers were good at filtering invalid issues and as accurate as developers in filling in missing issue attributes, they had more difficulty accurately pinpointing the relevant product. We expect that this work will highlight the importance of issue triage in software projects and will help design further studies on understanding and improving triage practices.', NULL, NULL, 'J. Xie and M. Zhou and A. Mockus', '2013 ACM / IEEE International Symposium on Empirical Software Engineering and Measurement', NULL, NULL, NULL, '10.1109/ESEM.2013.62', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'online front-ends;project management;public domain software;software engineering;Gnome;Mozilla;OSS projects;issue filtering;issue tracking data;missing information filling;relevant product determination;software projects;triage impact;Accuracy;Atmospheric measurements;Communities;Computer bugs;Particle measurements;Software;Software engineering', NULL, NULL, 'Oct', NULL, NULL, NULL, '247-250', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Impact of Triage: A Study of Mozilla and Gnome', NULL, NULL, NULL, '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Xie2013', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000551', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Xuan:2012:DPB:2337223.2337227', NULL, 'Piscataway, NJ, USA', NULL, 'Xuan, Jifeng and Jiang, He and Ren, Zhilei and Zou, Weiqin', 'Proceedings of the 34th International Conference on Software Engineering', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Zurich, Switzerland', NULL, NULL, NULL, NULL, '25--35', NULL, NULL, NULL, NULL, 'IEEE Press', NULL, NULL, NULL, NULL, NULL, 'ICSE ''12', 'Developer Prioritization in Bug Repositories', NULL, 'http://dl.acm.org/citation.cfm?id=2337223.2337227', NULL, '2012', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Xuan:2012:DPB:2337223.2337227', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000552', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Yu2012', 'During software evolution, new released versions still contain many bugs. One common scenario is that end users encounter regression faults and submit them to bug tracking systems. Different from in-house regression testing, typically only one test input is available, which passes the old version and fails the modified new version. To address the issue, delta debugging has been proposed for failure-inducing changes identification between two versions. Despite promising results, there are two practical factors that thwart the application of delta debugging: a large number of tests and misleading false positives. In this work, we present a combination of coverage analysis and delta debugging that automatically isolates failure-inducing changes. Evaluations on twelve real regression faults in GNU software demonstrate both the speed gain and effectiveness improvements. Moreover, a case study on libPNG and TCPflow indicates that our technique is comparable to peer techniques in debugging regressions faults.', NULL, NULL, 'K. Yu and M. Lin and J. Chen and X. Zhang', 'Automated Software Engineering (ASE), 2012 Proceedings of the 27th IEEE/ACM International Conference on', NULL, NULL, NULL, '10.1145/2351676.2351681', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program debugging;regression analysis;software fault tolerance;GNU software;TCPflow;bug tracking system;delta debugging;effectiveness improvement;failure-inducing change identification;libPNG;regression fault debugging;regression testing;software evolution;speed gain;Regression fault;automated debugging;coverage analysis;delta debugging;field failure', NULL, NULL, 'Sept', NULL, NULL, NULL, '20-29', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Practical isolation of failure-inducing changes for debugging regression faults', NULL, NULL, NULL, '2012', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Yu2012', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000554', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Zanetti2013', 'Efficient bug triaging procedures are an important precondition for successful collaborative software engineering projects. Triaging bugs can become a laborious task particularly in open source software (OSS) projects with a large base of comparably inexperienced part-time contributors. In this paper, we propose an efficient and practical method to identify valid bug reports which a) refer to an actual software bug, b) are not duplicates and c) contain enough information to be processed right away. Our classification is based on nine measures to quantify the social embeddedness of bug reporters in the collaboration network. We demonstrate its applicability in a case study, using a comprehensive data set of more than 700, 000 bug reports obtained from the Bugzilla installation of four major OSS communities, for a period of more than ten years. For those projects that exhibit the lowest fraction of valid bug reports, we find that the bug reporters'' position in the collaboration network is a strong indicator for the quality of bug reports. Based on this finding, we develop an automated classification scheme that can easily be integrated into bug tracking platforms and analyze its performance in the considered OSS communities. A support vector machine (SVM) to identify valid bug reports based on the nine measures yields a precision of up to 90.3% with an associated recall of 38.9%. With this, we significantly improve the results obtained in previous case studies for an automated early identification of bugs that are eventually fixed. Furthermore, our study highlights the potential of using quantitative measures of social organization in collaborative software engineering. It also opens a broad perspective for the integration of social awareness in the design of support infrastructures.', NULL, NULL, 'M. S. Zanetti and I. Scholtes and C. J. Tessone and F. Schweitzer', '2013 35th International Conference on Software Engineering (ICSE)', NULL, NULL, NULL, '10.1109/ICSE.2013.6606653', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'groupware;pattern classification;program debugging;project management;public domain software;social networking (online);support vector machines;BUGZILLA installation;OSS communities;SVM;actual software bug;automated classification scheme;bug categorization;bug report quality;bug reporters;bug tracking platforms;bug triaging procedures;collaboration network;collaborative software engineering projects;open source software communities;part-time contributors;social awareness;social embeddedness;social networks;social organization;support vector machine;Collaboration;Communities;Computer bugs;History;Social network services;Software;Support vector machines', NULL, NULL, 'May', NULL, NULL, NULL, '1032-1041', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Categorizing bugs with social networks: A case study on four open source software communities', NULL, NULL, NULL, '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Zanetti2013', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000555', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Zanetti2013a', 'Social organization and division of labor crucially influence the performance of collaborative software engineering efforts. In this paper, we provide a quantitative analysis of the relation between social organization and performance in Gentoo, an Open Source community developing a Linux distribution. We study the structure and dynamics of collaborations as recorded in the project''s bug tracking system over a period of ten years. We identify a period of increasing centralization after which most interactions in the community were mediated by a single central contributor. In this period of maximum centralization, the central contributor unexpectedly left the project, thus posing a significant challenge for the community. We quantify how the rise, the activity as well as the subsequent sudden dropout of this central contributor affected both the social organization and the bug handling performance of the Gentoo community. We analyze social organization from the perspective of network theory and augment our quantitative findings by interviews with prominent members of the Gentoo community which shared their personal insights.', NULL, NULL, 'M. S. Zanetti and I. Scholtes and C. J. Tessone and F. Schweitzer', 'Cooperative and Human Aspects of Software Engineering (CHASE), 2013 6th International Workshop on', NULL, NULL, NULL, '10.1109/CHASE.2013.6614731', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Linux;program debugging;public domain software;GENTOO community;Linux distribution;bug handling performance;bug tracking system;central contributor;collaboration dynamics;collaboration structure;collaborative software engineering;labor division;maximum centralization;network theory;open source community;social organization;Collaboration;Communities;Computer bugs;Network topology;Organizations;Peer-to-peer computing;Topology', NULL, NULL, 'May', NULL, NULL, NULL, '49-56', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'The rise and fall of a central contributor: Dynamics of social organization and performance in the GENTOO community', NULL, NULL, NULL, '2013', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Zanetti2013a', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000556', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Zhang2014a', 'Software products are increasingly complex, so it is becoming more difficult to find and correct bugs in large programs. Software developers rely on bug reports to fix bugs; thus, bug-tracking tools have been introduced to allow developers to upload, manage, and comment on bug reports to guide corrective software maintenance. However, the very high frequency of duplicate bug reports means that the triagers who help software developers in eliminating bugs must allocate large amounts of time and effort to the identification and analysis of these bug reports. In addition, classifying bug reports can help triagers arrange bugs in categories for the fixers who have more experience for resolving historical bugs in the same category. Unfortunately, due to a large number of submitted bug reports every day, the manual classification for these bug reports increases the triagers'' workload. To resolve these problems, in this study, we develop a novel technique for automatic duplicate detection and classification of bug reports, which reduces the time and effort consumed by triagers for bug fixing. Our novel technique uses a support vector machine to check whether a new bug report is a duplicate. The concept profile is also used to classify the bug reports into related categories in a taxonomic tree. Finally, we conduct experiments that demonstrate the feasibility of our proposed approach using bug reports extracted from the large-scale open source project Mozilla. Copyright © 2014 The Institute of Electronics, Information and Communication Engineers.', NULL, NULL, 'Zhang, T. and Lee, B.', NULL, NULL, NULL, NULL, '10.1587/transinf.E97.D.1756', NULL, NULL, NULL, NULL, NULL, NULL, 'IEICE Transactions on Information and Systems', NULL, NULL, NULL, NULL, NULL, 'cited By 0', '7', NULL, '1756-1768', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'A novel technique for duplicate detection and classification of bug reports', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84903701013&partnerID=40&md5=b2fbabe4eb85c94c1ba645bc2672f4ba', 'E97-D', '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Zhang2014a', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000557', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Zhang:2011:BRB:2055440.2055904', NULL, 'Washington, DC, USA', NULL, 'Zhang, Tao and Lee, Byungjeong', 'Proceedings of the 2011 IEEE 11th International Conference on Computer and Information Technology', NULL, NULL, NULL, '10.1109/CIT.2011.90', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'bug report classification, bug tracking system, developer feedback, software maintenance', NULL, NULL, NULL, NULL, NULL, NULL, '336--343', NULL, NULL, NULL, NULL, 'IEEE Computer Society', NULL, NULL, NULL, NULL, NULL, 'CIT ''11', 'A Bug Rule Based Technique with Feedback for Classifying Bug Reports', NULL, 'http://dx.doi.org/10.1109/CIT.2011.90', NULL, '2011', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Zhang:2011:BRB:2055440.2055904', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000558', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Zhang2014', 'When a bug is reported to the bug tracking system, it should be assigned to a developer responsible for its resolution after it is verified. This processing is also called bug triage. With increasing number of bug reports submitted to the bug tracking system, it is more and more difficult to assign appropriate developers to all the reported bugs manually. In this paper, we propose an approach called BUTTER (BUg Triage by topic modeling and heTERogeneous network analysis) to automatically assign bugs to developers. Different from other work, we regard that in most cases, bug resolution is a collaborative activity which involves many developers'' participation. Although social network analysis has been introduced to characterize the collaboration of developers, networks constructed by researchers are usually homogenous. That is, all the nodes and links in these networks are regarded as having same properties. Considering developers collaborated on different bugs, we construct a heterogeneous network that includes relationships between submitters, bugs and developers to characterize developers'' collaboration. Experiment shows that BUTTER outperforms other methods on automated bug triage.', NULL, NULL, 'W. Zhang and G. Han and Q. Wang', 'Cloud Computing and Big Data (CCBD), 2014 International Conference on', NULL, NULL, NULL, '10.1109/CCBD.2014.14', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program debugging;program verification;BUTTER;bug reports;bug resolution;bug tracking system;bug triage by topic modeling and heterogeneous network analysis;collaborative activity;developers collaboration;social network analysis;verification;Analytical models;Computer bugs;Dairy products;Mathematical model;Social network services;Software;Training;bug triage;heterogeneous network;topic model', NULL, NULL, 'Nov', NULL, NULL, NULL, '62-69', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'BUTTER: An Approach to Bug Triage with Topic Modeling and Heterogeneous Network Analysis', NULL, NULL, NULL, '2014', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Zhang2014', '1');
