INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000560', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Zhang:2013:HNA:2569431.2569763', NULL, 'Washington, DC, USA', NULL, 'Zhang, Wen and Wang, Song and Yang, Ye and Wang, Qing', 'Proceedings of the 2013 International Conference on Cloud and Service Computing', NULL, NULL, NULL, '10.1109/CSC.2013.23', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'bug triage, developer contribution, heterogeneous network', NULL, NULL, NULL, NULL, NULL, NULL, '98--105', NULL, NULL, NULL, NULL, 'IEEE Computer Society', NULL, NULL, NULL, NULL, NULL, 'CSC ''13', 'Heterogeneous Network Analysis of Developer Contribution in Bug Repositories', NULL, 'http://dx.doi.org/10.1109/CSC.2013.23', NULL, '2013', NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Zhang:2013:HNA:2569431.2569763', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000561', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Zhou2015', 'Motivation: To survive and succeed, FLOSS projects need contributors able to accomplish critical project tasks. However, such tasks require extensive project experience of long term contributors (LTCs). Aim: We measure, understand, and predict how the newcomers'' involvement and environment in the issue tracking system (ITS) affect their odds of becoming an LTC. Method: ITS data of Mozilla and Gnome, literature, interviews, and online documents were used to design measures of involvement and environment. A logistic regression model was used to explain and predict contributor''s odds of becoming an LTC. We also reproduced the results on new data provided by Mozilla. Results: We constructed nine measures of involvement and environment based on events recorded in an ITS. Macro-climate is the overall project environment while micro-climate is person-specific and varies among the participants. Newcomers who are able to get at least one issue reported in the first month to be fixed, doubled their odds of becoming an LTC. The macro-climate with high project popularity and the micro-climate with low attention from peers reduced the odds. The precision of LTC prediction was 38 times higher than for a random predictor. We were able to reproduce the results with new Mozilla data without losing the significance or predictive power of the previously published model. We encountered unexpected changes in some attributes and suggest ways to make analysis of ITS data more reproducible. Conclusions: The findings suggest the importance of initial behaviors and experiences of new participants and outline empirically-based approaches to help the communities with the recruitment of contributors for long-term participation and to help the participants contribute more effectively. To facilitate the reproduction of the study and of the proposed measures in other contexts, we provide the data we retrieved and the scripts we wrote at https://www.passion-lab.org/projects/developerfluency.html.', NULL, NULL, 'M. Zhou and A. Mockus', NULL, NULL, NULL, NULL, '10.1109/TSE.2014.2349496', NULL, NULL, NULL, NULL, NULL, NULL, 'IEEE Transactions on Software Engineering', NULL, 'behavioural sciences;project management;public domain software;FLOSS community;Free-Libre and/or open source software projects;Gnome;ITS data;LTC;Mozilla data;critical project;issue tracking system;logistic regression model;long term contributors;macroclimate;microclimate;open source software;Atmospheric measurements;Communities;Data mining;Data models;Electronic mail;Particle measurements;Predictive models;Long term contributor;extent of involvement;initial behavior;interaction with environment;issue tracking system;mining software repository;open source software', NULL, NULL, 'Jan', NULL, '1', NULL, '82-99', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Who Will Stay in the FLOSS Community? Modeling Participant Initial Behavior', NULL, NULL, '41', '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Zhou2015', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000562', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Zimmermann2009', 'Software development results in a huge amount of data: changes to source code are recorded in version archives, bugs are reported to issue tracking systems, and communications are archived in e-mails and newsgroups. We present techniques for mining version archives and bug databases to understand and support software development. First, we introduce the concept of co-addition of method calls, which we use to identify patterns that describe how methods should be called. We use dynamic analysis to validate these patterns and identify violations. The co-addition of method calls can also detect cross-cutting changes, which are an indicator for concerns that could have been realized as aspects in aspect-oriented programming. Second, we present techniques to build models that can successfully predict the most defect-prone parts of large-scale industrial software, in our experiments Windows Server 2003. This helps managers to allocate resources for quality assurance to those parts of a system that are expected to have most defects. The proposed measures on dependency graphs outperformed traditional complexity metrics. In addition, we found empirical evidence for a domino effect, i.e., depending on defect-prone binaries increases the chances of having defects.', NULL, NULL, 'T. Zimmermann', 'Software Maintenance, 2009. ICSM 2009. IEEE International Conference on', NULL, NULL, NULL, '10.1109/ICSM.2009.5306296', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'data mining;object-oriented programming;program debugging;software quality;Windows Server 2003;aspect-oriented programming;bug databases;large-scale industrial software;quality assurance;resource allocation;software development;source code;tracking systems;version archives mining;Computer bugs;Computer industry;Databases;Large-scale systems;Pattern analysis;Predictive models;Programming;Quality assurance;Quality management;Resource management', NULL, NULL, 'Sept', NULL, NULL, NULL, '443-446', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Changes and bugs Mining and predicting development activities', NULL, NULL, NULL, '2009', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Zimmermann2009', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000564', (SELECT entry_types_id FROM entry_types WHERE label='article'), 'Zimmermann2010', 'In software development, bug reports provide crucial information to developers. However, these reports widely differ in their quality. We conducted a survey among developers and users of APACHE, ECLIPSE, and MOZILLA to find out what makes a good bug report. The analysis of the 466 responses revealed an information mismatch between what developers need and what users supply. Most developers consider steps to reproduce, stack traces, and test cases as helpful, which are, at the same time, most difficult to provide for users. Such insight is helpful for designing new bug tracking tools that guide users at collecting and providing more helpful information. Our CUEZILLA prototype is such a tool and measures the quality of new bug reports; it also recommends which elements should be added to improve the quality. We trained CUEZILLA on a sample of 289 bug reports, rated by developers as part of the survey. The participants of our survey also provided 175 comments on hurdles in reporting and resolving bugs. Based on these comments, we discuss several recommendations for better bug tracking systems, which should focus on engaging bug reporters, better tool support, and improved handling of bug duplicates.', NULL, NULL, 'T. Zimmermann and R. Premraj and N. Bettenburg and S. Just and A. Schroter and C. Weiss', NULL, NULL, NULL, NULL, '10.1109/TSE.2010.63', NULL, NULL, NULL, NULL, NULL, NULL, 'IEEE Transactions on Software Engineering', NULL, 'program debugging;program testing;software quality;APACHE;CUEZILLA prototype;ECLIPSE;MOZILLA;bug tracking tools;software development;Computer bugs;Debugging;Engineering management;Human factors;Information analysis;Programming;Prototypes;Software engineering;Software maintenance;Software testing;Testing and debugging;and enhancement;distribution;human factors;maintenance;management;measurement.', NULL, NULL, 'Sept', NULL, '5', NULL, '618-643', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'What Makes a Good Bug Report?', NULL, NULL, '36', '2010', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Zimmermann2010', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000568', (SELECT entry_types_id FROM entry_types WHERE label='conference'), 'Zimmermann2009b', 'It is important that information provided in bug reports is relevant and complete in order to help resolve bugs quickly. However, often such information trickles to developers after several iterations of communication between developers and reporters. Poorly designed bug tracking systems are partly to blame for this exchange of information being stretched over time. Our paper addresses the concerns of bug tracking systems by proposing four broad directions for enhancements. As a proof-of-concept, we also demonstrate a prototype interactive bug tracking system that gathers relevant information from the user and identifies files that need to be fixed to resolve the bug. © 2009 IEEE.', NULL, NULL, 'Zimmermann, T.a b and Premraj, R.b and Sillito, J.c and Breu, S.d ', NULL, NULL, NULL, NULL, '10.1109/ICSE-COMPANION.2009.5070993', NULL, NULL, NULL, NULL, NULL, NULL, '2009 31st International Conference on Software Engineering - Companion Volume, ICSE 2009', NULL, NULL, NULL, NULL, NULL, 'cited By 11', NULL, NULL, '247-250', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Improving bug tracking systems', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-70349694449&partnerID=40&md5=be7a1ad461d14b326b5eb0459983cc56', NULL, '2009', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', 'Zimmermann2009b', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000569', (SELECT entry_types_id FROM entry_types WHERE label='inproceedings'), 'Zou2015', 'Bug fixing is one of the most important activities in software development and maintenance. A software project often employs an issue tracking system such as Bugzilla to store and manage their bugs. In the issue tracking system, many bugs are invalid but take unnecessary efforts to identify them. In this paper, we mainly focus on bug fixing rate, i.e., The proportion of the fixed bugs in the reported closed bugs. In particular, we study the characteristics of bug fixing rate and investigate the impact of a reporter''s different contribution behaviors to the bug fixing rate. We perform an empirical study on all reported bugs of two large open source software communities Eclipse and Mozilla. We find (1) the bug fixing rates of both projects are not high, (2) there exhibits a negative correlation between a reporter''s bug fixing rate and the average time cost to close the bugs he/she reports, (3) the amount of bugs a reporter ever fixed has a strong positive impact on his/her bug fixing rate, (4) reporters'' bug fixing rates have no big difference, whether their contribution behaviors concentrate on a few products or across many products, (5) reporters'' bug fixing rates tend to increase as time goes on, i.e., Developers become more experienced at reporting bugs.', NULL, NULL, 'W. Zou and X. Xia and W. Zhang and Z. Chen and D. Lo', 'Computer Software and Applications Conference (COMPSAC), 2015 IEEE 39th Annual', NULL, NULL, NULL, '10.1109/COMPSAC.2015.57', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'program debugging;public domain software;software maintenance;Eclipse;Mozilla;bug fixing rate;open source software community;software development;software maintenance;Computer aided software engineering;Computer bugs;Correlation;Entropy;History;Open source software;Bug Fixing Rate;Bug Reports;Empirical Study;Statistical Analysis', NULL, NULL, 'July', NULL, NULL, NULL, '254-263', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'An Empirical Study of Bug Fixing Rate', NULL, NULL, '2', '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, NULL, 'Zou2015', '1');
INSERT INTO entries (jabref_eid, entry_types_id, cite_key, abstract, address, annote, author, booktitle, chapter, comment, crossref, doi, edition, editor, eid, file, howpublished, institution, journal, key_, keywords, language, location, month, note, number, organization, pages, pdf, pmid, priority, ps, publisher, qualityassured, ranking, relevance, school, search, series, title, type, url, volume, year, date, __groupsearch, entrytype, timestamp, owner, __search, __markedentry, bibtexkey, database_id) VALUES ('00000570', (SELECT entry_types_id FROM entry_types WHERE label='article'), '2015', 'The proceedings contain 38 papers. The special focus in this conference is on Search-Based Software Engineering. The topics include: Genetic improvement of software for multiple objectives; amortised optimisation of non-functional properties in production environments; metrics are not enough; searching for useful parallelism in functional programs; an improved beam-search for the test case generation for formal verification systems; combining multiple coverage criteria in search-based unit test generation; epistatic genetic algorithm for test case prioritization; a scala combinator toolkit for semi-automated composition of metaheuristics; hypervolume-based search for test case prioritization; optimizing aspect-oriented product line architectures with search-based algorithms; adaptive neighbourhood search for the component deployment problem; transformed search based software engineering; regression test case prioritisation for guava; continuous test generation on guava; generating readable unit tests for guava; testing django configurations using combinatorial interaction testing; synthesis of equivalent method calls in guava; object-oriented genetic improvement for improved energy consumption in google guava; automated transplantation of call graph and layout features into kate; growing django citation services using SBSE; multi-objective module clustering for kate; search based component selection for budget hardware; search-based bug report prioritization for kate editor bugs repository; inferring test models from kate’s bug reports using multi-objective search; introducing learning mechanism for class responsibility assignment problem; transformed vargha-delaney effect size; optimizing software product line architectures with OPLA-tool; exploring the landscape of non-functional program properties using spatial analysis and interactive software release planning with preferences base.', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, 'Lecture Notes in Computer Science (including subseries Lecture Notes in Artificial Intelligence and Lecture Notes in Bioinformatics)', NULL, NULL, NULL, NULL, NULL, 'cited By 0', NULL, NULL, '1-354', NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, NULL, '7th International Symposium on Search-Based Software Engineering, SSBSE 2015', NULL, 'https://www.scopus.com/inward/record.uri?eid=2-s2.0-84951276529&partnerID=40&md5=2ddd99abeb9b2d0bb59d68811edc6a6b', '9275', '2015', NULL, NULL, NULL, '2016.06.08', 'vagner', NULL, '[vagner:]', '2015', '1');
